INSERT INTO `meetup_memory_data` VALUES (/*id*/181, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)④", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"風きもちー、空気もうめー !\nパイセンも一緒に深呼吸♪\nすー、はー☆");
