INSERT INTO `meetup_memory_data` VALUES (/*id*/185, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑧", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"お祭りではお面つけるんだって。\nでも急だから、はぁとたちのはないみたい。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/186, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/0, /*description*/"びっくりしたな。村に入った途端、めっちゃもてなされたもん。\n村に若者が来たから安泰とか言われたし。\nなんか村の神様？ も満足するんだとか。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/187, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"やっぱ山の奥なだけあって、外から観光に来る人も\n少ないらしくってさ。久々のお客さんだからって\nめっちゃ美味しい料理用意してくれたわ。スウィーティー♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/188, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"聞いた？ プロデューサー、聞いた？ 若者だって !\n若くてきれいなお嬢さんだって ! やっぱわかる人には\nわかっちゃうなー。はぁともまだまだぴちぴちだって☆");
INSERT INTO `meetup_memory_data` VALUES (/*id*/189, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/12, /*description*/"最初は遠慮があったけど、今はいろんな人と仲良くなったし。\nおもてなしのお礼に、しゅがみんから一曲どーよ？\nよし、プロデューサーからOK出たし、歌うか、パイセン !");
INSERT INTO `meetup_memory_data` VALUES (/*id*/190, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"さっき、この家の外れで看板見つけてさ。\nどこに案内する看板か気になったんだけど、\n文字がかすれてて読めなかったわ。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/191, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"で、村の人に聞いたら、ここの名所は温泉と\nたくさんある祠くらいなんだってさ。\n小さい村だから、すぐ見て回れるらしいし、あとで行くか !");
INSERT INTO `meetup_memory_data` VALUES (/*id*/192, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"はぁとにはわかる ! あの看板が指し示すのは……\nデートスポットになる絶景☆ ってことで～、\nプロデューサー、このあとふたりで行ってみる？");
INSERT INTO `meetup_memory_data` VALUES (/*id*/193, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"村の人に聞いてみたんだけど、知らないって言われたんだよね。\n誰も知らない、まだ見ぬ場所……これ、番組的にチャンスじゃん？\nあとで菜々パイセンも誘って行ってみよーっと !");
INSERT INTO `meetup_memory_data` VALUES (/*id*/194, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"事務所で配れるお土産、何かないかなって聞いたら\nこれもらっちった ! 村に代々伝わる工芸品らしくて、\nお守りにして持っとくといいんだって。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/195, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"んー、中身なんだろ？ もちろん取り出したりする気は\nないんだけどさ。なーんか甘い香りしてるし。\nドライフラワーでも入ってんのかなー？");
INSERT INTO `meetup_memory_data` VALUES (/*id*/196, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"たくさんもらったから、事務所のみんなの分はあるよ。\n裁縫道具もちゃんと持ってきてるし、ひとりひとりの\n個性に合わせて、はぁとが可愛～くデコっちゃう♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/197, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"プロデューサーの分もおそろいでもらってきた☆\nもちろん、はぁとがちゃんと刺繍してあげたぞ !\n「あい らぶ しゅがーはぁと」って♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/198, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"秘境っていうだけあって、ちょっと変わった場所ではあるよね。\nここにしかない伝統とか、言い伝えとかいっぱいあって。\nレポも兼ねて、パイセンとばっちり調査してたの。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/199, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"村長さんに「あの祠に行ったんか」って言われたから、\nチョコバーお供えしてきたって答えといた。\nよくわからんけど感謝されちった♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/200, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"子どもたちに、村に伝わる歌を教えてもらったんだよね。\n何語かわかんない歌だったけど。お返しに、『凸凹スピードスター』を\n教えといた。もちろん、振りもつけて☆");
INSERT INTO `meetup_memory_data` VALUES (/*id*/201, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/12, /*description*/"ここがどんな村だったとしてもさ、3人でなら\nなんとかなるっしょって思ってる。今までもそうしてきたし。\nもしかしなくても、はぁとたちってば無敵かも♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/202, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/1, /*description*/"ふぁ～……ちょいねむぅ……。\nあれだけ頑張って山の中進んできたからかなー……。\nん……ちょっと気、抜けちゃったわ。");
INSERT INTO `meetup_memory_data` VALUES (/*id*/203, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/4, /*description*/"山で叫んで疲れたし、いっぱい食べたら眠くなってきて……\nちっがーう ! はぁとは赤ちゃんでも牛でもなーい !\nプロデューサーの可愛いアイドル☆ りぴーとあふたみー♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/204, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"プロデューサー、ちょっとここに座ってみて？\nそれで……ごろーん☆\nほれほれ、今こそはぁとを膝枕で甘やかして？ ね？");
INSERT INTO `meetup_memory_data` VALUES (/*id*/205, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/5, /*description*/"でも、疲れてるのはプロデューサーも同じでしょ？\nほら、肩貸して？ はぁとはいつも鍛えてるから体力あるし。\n時間が来たら、優しく起こしてあげる……♪");
INSERT INTO `meetup_memory_data` VALUES (/*id*/206, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"んー、今ね、撮ってた映像見返してたとこ。\nプロデューサーも、可愛いはぁと、チェックしたい？\n……おい、首を横に振んな☆");
INSERT INTO `meetup_memory_data` VALUES (/*id*/207, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/4, /*description*/"おぉう、めっちゃブレてる映像あるなー。\nこれは泥に足を滑らせてパイセンと転んだときのね。\n水も泥も滴ってこそのいい女だけど……あんま見んな☆");
INSERT INTO `meetup_memory_data` VALUES (/*id*/208, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"ま、プロデューサーにとっては今さらかな。\nずっと近くではぁとたちのこと見てたわけだし？\nんもう、いいとこどりしちゃってこのこの～☆");
INSERT INTO `meetup_memory_data` VALUES (/*id*/209, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/5, /*description*/"とか言って、いざ見始めると誰よりも真剣な顔して\nチェックすんの、プロデューサーは。んで、その横顔\n見てると思うんだよね。すっごく愛されてるなって。");
