INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6022, /*comment_id*/100995, /*use_type*/0, /*index*/1, /*description*/"怪我のないよう、楽しみましょうね。清良との約束です♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6022, /*comment_id*/100995, /*use_type*/0, /*index*/2, /*description*/"ひとりきりじゃありません。ともに進むこと、それが大切です", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6022, /*comment_id*/100995, /*use_type*/0, /*index*/3, /*description*/"アイドルはさまざまな姿になってこそ…。ワクワクしますね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6022, /*comment_id*/100995, /*use_type*/1, /*index*/1, /*description*/"高い目標を目指して、動物のように力強く…ぴょん♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6022, /*comment_id*/100995, /*use_type*/2, /*index*/1, /*description*/"時には冷酷に…。あるいはそれも、ひとつの優しさになるから", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6022, /*comment_id*/100995, /*use_type*/3, /*index*/1, /*description*/"元気は大事な笑顔の素。足りないなら、お注射しましょう♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6022, /*comment_id*/100995, /*use_type*/4, /*index*/1, /*description*/"みんなの健やかな姿が、私をもっと元気にしてくれるんです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6022, /*comment_id*/100995, /*use_type*/5, /*index*/1, /*description*/"たくさんの可能性がある…胸の高鳴りは止められませんね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6022, /*comment_id*/100995, /*use_type*/100, /*index*/1, /*description*/"目的地は、もうすぐそこ。楽しい時間はあっという間です", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6022, /*comment_id*/100995, /*use_type*/101, /*index*/1, /*description*/"慣れないことも、少しずつ。いずれは胸を張れるように…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6022, /*comment_id*/100995, /*use_type*/102, /*index*/1, /*description*/"コツを掴んだら、モノにするチャンス…今がその時ですね。\n照準を進行方向に合わせたら、まっすぐ進んでいきましょう。\n私たちという弾丸を防ぐ壁をも、貫いて。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6022, /*comment_id*/100995, /*use_type*/102, /*index*/2, /*description*/"ふふっ…楽しい、楽しいって、心がずっと弾んでいます♪\n私が積み重ねてきたもの、歩んできた道…そのすべてが、\nキラキラ輝く、愛おしい宝物なんですよ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6022, /*comment_id*/100995, /*use_type*/102, /*index*/3, /*description*/"私たちの連携、とても強固なものになってきました。\nこの絆は、何者にも弛ませられないよう守り通します。\nともに進む相棒として…背中は私にお任せを。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6022, /*comment_id*/300981, /*use_type*/0, /*index*/1, /*description*/"遊び回って笑い合う ! お祭りってのは楽しいね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6022, /*comment_id*/300981, /*use_type*/0, /*index*/2, /*description*/"ピタリ狙い通りなら、どうぞ拍手喝采を ! なんてね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6022, /*comment_id*/300981, /*use_type*/0, /*index*/3, /*description*/"一緒に進もう ! 旅は道連れ、きっともっと楽しくなるよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6022, /*comment_id*/300981, /*use_type*/1, /*index*/1, /*description*/"浴衣に似合うのは笑顔に決まってらい ! にー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6022, /*comment_id*/300981, /*use_type*/2, /*index*/1, /*description*/"狙う時は冷静に…真剣に楽しんでこそのお祭りさ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6022, /*comment_id*/300981, /*use_type*/3, /*index*/1, /*description*/"元気なら売るほど余ってるよ ! まだまだ遊ぼう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6022, /*comment_id*/300981, /*use_type*/4, /*index*/1, /*description*/"みんなで作ったお祭りなんだ。みんなで楽しまないとね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6022, /*comment_id*/300981, /*use_type*/5, /*index*/1, /*description*/"よし来ーい ! どんなのだって歌ってみせらあ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6022, /*comment_id*/300981, /*use_type*/100, /*index*/1, /*description*/"あと少しだね ! 最後まで全部楽しんでこ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6022, /*comment_id*/300981, /*use_type*/101, /*index*/1, /*description*/"へへっ、どんな難問だって、撃ち抜いてみせるよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6022, /*comment_id*/300981, /*use_type*/102, /*index*/1, /*description*/"おっと、こりゃあチャンス到来だ !\nこの機を逃さず、どんどん進んでみようよ !\nきっと気持ちよくって楽しいよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6022, /*comment_id*/300981, /*use_type*/102, /*index*/2, /*description*/"みんなの笑顔で力がみなぎってくるよ !\nここで立ち止まってなんかいられないね !\n行けるところまで行ってみようか !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6022, /*comment_id*/300981, /*use_type*/102, /*index*/3, /*description*/"次はどんなワクワクが待ってるのか、楽しみだね !\n興奮しちゃって、もう待ちきれないかも !\n行こう ! ワクワクがあたしたちを待ってるよ !", /*insert_word_type*/0);
