INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6021, /*comment_id*/200997, /*use_type*/3, /*index*/1, /*description*/"活発に動けるだけの体力は保っている。息も、切らさない", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6021, /*comment_id*/200997, /*use_type*/4, /*index*/1, /*description*/"どんな夜空でも輝き、旅人を導く星々に…なりたいものね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6021, /*comment_id*/200997, /*use_type*/5, /*index*/1, /*description*/"歌に乗せるのは、祈り。何よりも…心に届く音色を", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6021, /*comment_id*/200997, /*use_type*/100, /*index*/1, /*description*/"あと少しだからこそ、気を引き締めて。最後まで盤石に", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6021, /*comment_id*/200997, /*use_type*/101, /*index*/1, /*description*/"あくなき探究心は、人を成長させる一番の欲求よ…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6021, /*comment_id*/200997, /*use_type*/102, /*index*/1, /*description*/"今が好機。出力を上げていきましょう。\n機会を窺うあまり、しり込みをしてはいけないわ。\nやる時は全力で…そして駆け抜けるのよ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6021, /*comment_id*/200997, /*use_type*/102, /*index*/2, /*description*/"周りの光が明るすぎると、自分の輝きは見えなくなってしまう。\nそんな時は、今までの軌跡を思い出すわ。\n輝きを発するに足る努力を…私も、してきたのだから。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6021, /*comment_id*/200997, /*use_type*/102, /*index*/3, /*description*/"かつては現状に甘んじて…自分の可能性を狭めていた。\n限界値は、自分の想像を超えた先にある。\n壁を打ち破って、共に高みへ至りましょう。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6021, /*comment_id*/100969, /*use_type*/0, /*index*/1, /*description*/"多くの方に、大きな幸いを届けられるよう、歩み…歌いますわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6021, /*comment_id*/100969, /*use_type*/0, /*index*/2, /*description*/"目標は高く…道のりが長くとも、得られるものは大きいはず", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6021, /*comment_id*/100969, /*use_type*/0, /*index*/3, /*description*/"愛おしい日々を振り返れるよう、思い出を紡いでいきましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6021, /*comment_id*/100969, /*use_type*/1, /*index*/1, /*description*/"少しお腹が空いてきましたわ…今日のご飯は何にしましょう…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6021, /*comment_id*/100969, /*use_type*/2, /*index*/1, /*description*/"指導者らしくキリっと…あら、自然と笑顔になってしまいます", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6021, /*comment_id*/100969, /*use_type*/3, /*index*/1, /*description*/"遥かな高みを目指して…クラリス、目いっぱい頑張りますわ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6021, /*comment_id*/100969, /*use_type*/4, /*index*/1, /*description*/"皆さんの心の音色を重ねれば、とても美しい歌が響くはずです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6021, /*comment_id*/100969, /*use_type*/5, /*index*/1, /*description*/"どの曲にも、祈りと想いを込めて歌い上げましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6021, /*comment_id*/100969, /*use_type*/100, /*index*/1, /*description*/"たゆまぬ努力が実を結びましたわね。あと少し、ですわ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6021, /*comment_id*/100969, /*use_type*/101, /*index*/1, /*description*/"いかなる試練も乗り越えましょう。先へと、歩むために", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6021, /*comment_id*/100969, /*use_type*/102, /*index*/1, /*description*/"身も心も、弾むメロディーのようにとても軽やかですわ♪\nいつもより、もっともっと進んでいけそうな…\nそんな吉兆を感じます。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6021, /*comment_id*/100969, /*use_type*/102, /*index*/2, /*description*/"必ず祈りは天に届き、光さす道が現れますわ。\n貴方がそばにいてくだされば、その道を歩んでいける。\n求めるものがどんなに遠くとも、ふたりでならば、きっと。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6021, /*comment_id*/100969, /*use_type*/102, /*index*/3, /*description*/"これまでの歩みは、心にたくさん積もった宝物…。\nこの先にも、きっとさらなる輝きがあるはずですわ。\n幸いと愛に満ちた場所を、ともに目指しましょう。", /*insert_word_type*/0);
