INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6033, /*comment_id*/201281, /*use_type*/0, /*index*/1, /*description*/"結婚とか恋愛とか…難しいけど、進んだ先に答えがあるよね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6033, /*comment_id*/201281, /*use_type*/0, /*index*/2, /*description*/"誰かと一緒に道を歩く…すごく頼もしくて、心強いよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6033, /*comment_id*/201281, /*use_type*/0, /*index*/3, /*description*/"未来はわからない。だからこそ、進むのが楽しいんだよね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6033, /*comment_id*/201281, /*use_type*/1, /*index*/1, /*description*/"みんなをキュンとさせるから、覚悟してよね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6033, /*comment_id*/201281, /*use_type*/2, /*index*/1, /*description*/"結婚式だからって、緊張してちゃダメだよね。冷静にいこう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6033, /*comment_id*/201281, /*use_type*/3, /*index*/1, /*description*/"何事も勢いが大事 ! 情熱的な演技、見せてあげるよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6033, /*comment_id*/201281, /*use_type*/4, /*index*/1, /*description*/"たくさんの人が見守ってくれてる。だからきっと、大丈夫♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6033, /*comment_id*/201281, /*use_type*/5, /*index*/1, /*description*/"好きなように動こう ! アタシたちが主役なんだから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6033, /*comment_id*/201281, /*use_type*/100, /*index*/1, /*description*/"あともう少しでひと区切りだね。張り切って進むよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6033, /*comment_id*/201281, /*use_type*/101, /*index*/1, /*description*/"よーし、頑張ってクリアしよう ! 何事も挑戦あるのみだよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6033, /*comment_id*/201281, /*use_type*/102, /*index*/1, /*description*/"予感がするんだ。もっと前に進めるって !\nお淑やかに歩くのは、バージンロードにとっておいて……\n今はアタシたちのペースで、思いっきり走り抜けよっか♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6033, /*comment_id*/201281, /*use_type*/102, /*index*/2, /*description*/"みんなが見守って、助けてくれる。\nそれだけ、期待と信頼を寄せてくれてるんだよね。\nよーし、その気持ちに応えてみせるっ ! 行くよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6033, /*comment_id*/201281, /*use_type*/102, /*index*/3, /*description*/"たくさん考えて、たくさん頑張ってきたんだ。だからわかるよ。\nアタシたちなら、踊るように、自由に、どこまでも行ける !\nほら、一緒に ! 行けるところまで行ってみようよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6033, /*comment_id*/101243, /*use_type*/0, /*index*/1, /*description*/"花嫁になるなんてびっくりです。地元のお仕事は嬉しいけども", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6033, /*comment_id*/101243, /*use_type*/0, /*index*/2, /*description*/"ご指名でいただいたお仕事…期待にはちゃんと応えてぇです !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6033, /*comment_id*/101243, /*use_type*/0, /*index*/3, /*description*/"大丈夫、大丈夫…わだすには、みんながいてくれっから", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6033, /*comment_id*/101243, /*use_type*/1, /*index*/1, /*description*/"可愛くも撮ってもらいてぇな。せっかくの花嫁衣装ですし", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6033, /*comment_id*/101243, /*use_type*/2, /*index*/1, /*description*/"お淑やかな表情も…。へへ、写真見ると別人になった気分です", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6033, /*comment_id*/101243, /*use_type*/3, /*index*/1, /*description*/"衣装さ負げねぇように躍動感を ! 動画の撮影もありますし !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6033, /*comment_id*/101243, /*use_type*/4, /*index*/1, /*description*/"誰かと比べてとかでねぇ、わだすだけの魅力…探していきます", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6033, /*comment_id*/101243, /*use_type*/5, /*index*/1, /*description*/"立派にやってるって、見せてあげたい人たちがいるんです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6033, /*comment_id*/101243, /*use_type*/100, /*index*/1, /*description*/"やりきるって決めましたから…あとは踏み出すだけです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6033, /*comment_id*/101243, /*use_type*/101, /*index*/1, /*description*/"地元の人たちさ胸張れるお仕事を、きっと見せますから… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6033, /*comment_id*/101243, /*use_type*/102, /*index*/1, /*description*/"わだすは…わだすひとりでアイドルがでぎるほど強ぐねぇです。\nんだども、応援してくれる人がいて、\n励ましてくれる人がいるから、わだすは前を向けるんです !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6033, /*comment_id*/101243, /*use_type*/102, /*index*/2, /*description*/"わだすに花嫁衣装の撮影がこなせるかなって思ったんだども、\nみんなが、最高のものを一生懸命作ろうとしてくれてます…。\nだったら、わだすも期待さ応えねぇど… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6033, /*comment_id*/101243, /*use_type*/102, /*index*/3, /*description*/"みんなの気持ち、ちゃんと感じてます。\n力がもりもり湧いてきて、\n今ならわだす、どこまでも行けそうな気がします… !", /*insert_word_type*/0);
