INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6030, /*comment_id*/101165, /*use_type*/2, /*index*/1, /*description*/"素敵な温泉で心を落ち着かせてから、挑んでいきましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6030, /*comment_id*/101165, /*use_type*/3, /*index*/1, /*description*/"体の芯まであたたまる、そんなあたしをお見せしますからね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6030, /*comment_id*/101165, /*use_type*/4, /*index*/1, /*description*/"みなさんと温泉に入って元気満タン ! さぁ、行きますよー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6030, /*comment_id*/101165, /*use_type*/5, /*index*/1, /*description*/"美肌に疲労回復…いろんな効能があってなんだかお得です♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6030, /*comment_id*/101165, /*use_type*/100, /*index*/1, /*description*/"特別…安らぎ…。あと少しでなにか掴めそうな気がします !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6030, /*comment_id*/101165, /*use_type*/101, /*index*/1, /*description*/"パンで栄養補給したあたしたちなら、絶対クリアできますよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6030, /*comment_id*/101165, /*use_type*/102, /*index*/1, /*description*/"温泉とパンが、あたしに力をくれました… !\n体がすごく熱くって…\nずーっと先まで走っていけちゃいそうですっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6030, /*comment_id*/101165, /*use_type*/102, /*index*/2, /*description*/"あははー ! なんだか調子が上がってきましたよー !\n体がぽかぽかで、お肌ももっちもちで…\nなるほど、これが温泉の効能なんですねっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6030, /*comment_id*/101165, /*use_type*/102, /*index*/3, /*description*/"汗をいっぱい流してスッキリ !\nパンをいっぱい食べて大満足 !\n元気もやる気も満タン、張り切って進みましょうー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6030, /*comment_id*/301147, /*use_type*/0, /*index*/1, /*description*/"行きましょうっ。ワクワクすることが待ってますよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6030, /*comment_id*/301147, /*use_type*/0, /*index*/2, /*description*/"最後まで走りきるには、ペース配分が大事ですよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6030, /*comment_id*/301147, /*use_type*/0, /*index*/3, /*description*/"私も隣で一緒に走ります ! 頑張りましょうね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6030, /*comment_id*/301147, /*use_type*/1, /*index*/1, /*description*/"しっとりしつつ可愛い微笑みを…少し硬いですかね？えへへ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6030, /*comment_id*/301147, /*use_type*/2, /*index*/1, /*description*/"よーし、カッコよく決めますよ ! 任せてくださいっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6030, /*comment_id*/301147, /*use_type*/3, /*index*/1, /*description*/"力いっぱい、元気よく ! 私の情熱、見せていきますから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6030, /*comment_id*/301147, /*use_type*/4, /*index*/1, /*description*/"みんなで一緒に体を動かすと、楽しいですよね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6030, /*comment_id*/301147, /*use_type*/5, /*index*/1, /*description*/"興味があることは、とりあえず全部やってみましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6030, /*comment_id*/301147, /*use_type*/100, /*index*/1, /*description*/"たくさん汗をかいたあとは…温泉が待ってますよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6030, /*comment_id*/301147, /*use_type*/101, /*index*/1, /*description*/"燃えてきましたねー ! あっ、水分補給もお忘れなく !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6030, /*comment_id*/301147, /*use_type*/102, /*index*/1, /*description*/"心も体も、ぽっかぽか♪\n今ならもっといい汗をかけちゃいそうです !\nさぁ、思いっきり走って行きますよーっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6030, /*comment_id*/301147, /*use_type*/102, /*index*/2, /*description*/"ん～…いい気持ちです♪\n私たちには、ちょっと熱いくらいがちょうどいいのかも。\nふふっ、熱が冷めないうちにもっと進みましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6030, /*comment_id*/301147, /*use_type*/102, /*index*/3, /*description*/"体があったまってきました… !\nこれまで流してきた汗は裏切りません。もう一度ここで\n気合を入れ直して、一気にゴールを目指していきましょうね !", /*insert_word_type*/0);
