INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6043, /*comment_id*/101355, /*use_type*/0, /*index*/1, /*description*/"カワイイの道は、ひとりのボクから ! 覚えてください ! ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6043, /*comment_id*/101355, /*use_type*/0, /*index*/2, /*description*/"ヘレンさんに呼ばれたような…え？踊りに来てほしい…？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6043, /*comment_id*/101355, /*use_type*/0, /*index*/3, /*description*/"ボク、輿水幸子が降り立った島は…そう、カワイイ幸子島 !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6043, /*comment_id*/101355, /*use_type*/1, /*index*/1, /*description*/"今のボクに、全世界が大号泣です。健気で、カワイすぎて", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6043, /*comment_id*/101355, /*use_type*/2, /*index*/1, /*description*/"何事にもクールな対応を…たとえヤドカリが出ても… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6043, /*comment_id*/101355, /*use_type*/3, /*index*/1, /*description*/"今日も元気に体を張って…まるでバラエティアイドルですねぇ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6043, /*comment_id*/101355, /*use_type*/4, /*index*/1, /*description*/"ひとりでも、感じてます…ボクを称賛する、みなさんの声を !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6043, /*comment_id*/101355, /*use_type*/5, /*index*/1, /*description*/"カワイさ、足りてますか ! ？ボクが満たしてあげましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6043, /*comment_id*/101355, /*use_type*/100, /*index*/1, /*description*/"ゴールさん、カワイイボクが行きますよ。覚悟してください !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6043, /*comment_id*/101355, /*use_type*/101, /*index*/1, /*description*/"過酷なロケにも負けません ! カワイイは強い、ですからね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6043, /*comment_id*/101355, /*use_type*/102, /*index*/1, /*description*/"ボクは早く帰らないといけません。じゃないと…\n世界にカワイイ成分が不足して、みんなが寂しくなっちゃいます。\nカワイイボクには、みんなを幸せにする義務がありますからね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6043, /*comment_id*/101355, /*use_type*/102, /*index*/2, /*description*/"どうです？サバイバルを完全にマスターしたボクの姿は !\n漁、狩り、火起こし。なんでもござれ。猛スピードでこなします !\n伊達にバラエティ慣れしてないんですよ、こっちは !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6043, /*comment_id*/101355, /*use_type*/102, /*index*/3, /*description*/"急に速く進めるようになるなんて、人間って不思議…\nいえ、よく考えたら当然ですね ! ボクったらうっかりさん♪\nボクのカワイさに興奮したら、どんな人も俊足になりますよね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6043, /*comment_id*/201421, /*use_type*/0, /*index*/1, /*description*/"ヘレンを探す旅を始めましょう。これが、私たちのジャーニー", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6043, /*comment_id*/201421, /*use_type*/0, /*index*/2, /*description*/"風も空も海もつながっている…地球とは、ひとつの家なのね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6043, /*comment_id*/201421, /*use_type*/0, /*index*/3, /*description*/"ふとした瞬間、誰かと通じ合っている…そんな気がするの…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6043, /*comment_id*/201421, /*use_type*/1, /*index*/1, /*description*/"髪にヤモリがついていたみたい。ガーディアンなのかしら", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6043, /*comment_id*/201421, /*use_type*/2, /*index*/1, /*description*/"現場に穴は開けないわ。人が足りなければふたりになればいい", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6043, /*comment_id*/201421, /*use_type*/3, /*index*/1, /*description*/"1日は案外短い…ダンスは1セット48時間にしましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6043, /*comment_id*/201421, /*use_type*/4, /*index*/1, /*description*/"聞こえる…世界の声が。ヘレンの声が。私は…飛べる ! !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6043, /*comment_id*/201421, /*use_type*/5, /*index*/1, /*description*/"すべての道はヘレンに通ず…ふと浮かんできたの、この言葉が", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6043, /*comment_id*/201421, /*use_type*/100, /*index*/1, /*description*/"ともに、ゴールテープを切りに行きましょう。踊りながらね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6043, /*comment_id*/201421, /*use_type*/101, /*index*/1, /*description*/"単独登頂よりは楽ですよ。だって…ヘレンはふたりいるから", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6043, /*comment_id*/201421, /*use_type*/102, /*index*/1, /*description*/"日常のなかで世界を考える機会は少ないわ。\nならばいま、ここで、考えるんです。自分自身と向き合って。\nそうすれば…ほら、光明が見えてきたでしょう？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6043, /*comment_id*/201421, /*use_type*/102, /*index*/2, /*description*/"踊るたびに、私のなかに何かが生まれている…これは、星？\nまるでビッグバンね…ダンサブルとは、つまり、宇宙…？\nようやくつかんできた気がする…「ヘレン」を… !\n", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6043, /*comment_id*/201421, /*use_type*/102, /*index*/3, /*description*/"私はヘレンで、ヘレンはヘレン。そして…世界よ。\n常識に囚われてはいけないわ。人はいつだって突飛でなくてはね。\nさぁ、イマジネーションを ! もうじき、大気圏を突破するわ ! !", /*insert_word_type*/0);
