INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6016, /*comment_id*/200869, /*use_type*/100, /*index*/1, /*description*/"あの先に幸せがあるのね ! ならあとは、まっすぐ進むだけ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6016, /*comment_id*/200869, /*use_type*/101, /*index*/1, /*description*/"さすがにちょっと緊張…でも、あたしひとりじゃないものね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6016, /*comment_id*/200869, /*use_type*/102, /*index*/1, /*description*/"ふふふ…見えたわ ! これからあたしたちには、\nとっておきのチャンスが巡ってくる !\nこのお告げは絶対に外れないわよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6016, /*comment_id*/200869, /*use_type*/102, /*index*/2, /*description*/"順調順調 ! 今日のあたしってばついてるわね♪\nこの幸運、逃がすつもりはないわよ !\nしっかり掴んで、みんなをもっと幸せにするんだから !", /*insert_word_type*/0);
