INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6041, /*comment_id*/201405, /*use_type*/4, /*index*/1, /*description*/"喜びも、愛しさも…沈めた思い出も。海は全て知ってるわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6041, /*comment_id*/201405, /*use_type*/5, /*index*/1, /*description*/"海はいろいろな顔を見せてくれる…私がまだ知らない顔も…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6041, /*comment_id*/201405, /*use_type*/100, /*index*/1, /*description*/"もうすぐよ。この先に貴方に見せたい、美しい景色があるの…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6041, /*comment_id*/201405, /*use_type*/101, /*index*/1, /*description*/"どんな課題も一歩ずつ。まずは顔を水につけるところから…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6041, /*comment_id*/201405, /*use_type*/102, /*index*/1, /*description*/"波の彼方には何があるのかしら、っていつも不思議で。\nけれど、ひとりでは勇気がなくて…探しに行けなかった。\n行ってくれる？貴方となら…深海の宝物も手に入りそうだわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6041, /*comment_id*/201405, /*use_type*/102, /*index*/2, /*description*/"ふぅ…少し、はしゃぎすぎたかしら。\n貴方も同じように楽しんでくれているから、つい…。\n不思議ね、ふたりだと際限なく…新しい私に出会えるわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6041, /*comment_id*/201405, /*use_type*/102, /*index*/3, /*description*/"お願い…手を離さないで。もっと強く、ひいてほしいの。\n貴方は教えてくれた…新しい海を。光り輝く海を。\n私が立ちすくんでしまう荒波でさえも、乗り越えさせて… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6041, /*comment_id*/101335, /*use_type*/0, /*index*/1, /*description*/"何事も積み重ねですよねぇ。早耶、頑張りまぁす♪", /*insert_word_type*/0);
