INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6041, /*comment_id*/101335, /*use_type*/0, /*index*/3, /*description*/"…大丈夫。失敗つづきでも、早耶は…ちゃんと立ててます", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6041, /*comment_id*/101335, /*use_type*/1, /*index*/1, /*description*/"早耶にお任せですよぉ♪一番カワイイとこ、見せるねぇ☆", /*insert_word_type*/0);
