INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6041, /*comment_id*/101335, /*use_type*/101, /*index*/1, /*description*/"よぉし、本気出しちゃいますよぉ♪早耶に夢中にさせちゃう☆", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6041, /*comment_id*/101335, /*use_type*/102, /*index*/1, /*description*/"早耶ね、もっともっとカワイくなりたいんです。…誰よりも。\nカワイくなりたいって気持ちを、誰にも邪魔されたくないの。\nそれが…早耶の信念ってやつ、なのかなぁ？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6041, /*comment_id*/101335, /*use_type*/102, /*index*/2, /*description*/"プロデューサーさんって、そんなに甘やかしてくれませんよねぇ。\nそういうところも好きですよぉ、信頼されてるって感じで♪\nだからぁ、ちゃぁんと応えちゃいます ! ほら、ついてきてぇ☆", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6041, /*comment_id*/101335, /*use_type*/102, /*index*/3, /*description*/"感じます、胸のあったかさ…早耶を想ってくれる人が、\nこんなにたくさんいてくれるんだってこと。\nうん…失敗なんて、もう怖くないです。まだまだ歩けますよぉ♪", /*insert_word_type*/0);
