INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6039, /*comment_id*/201387, /*use_type*/102, /*index*/1, /*description*/"長く生きてるとわかってくるのよね、ここだっていう瞬間が。\nこれまで多くのチャンスを、私は掴みとってきたの。\nつまり…今がそのときよ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6039, /*comment_id*/201387, /*use_type*/102, /*index*/2, /*description*/"ひとりでしか出せない力、仲間からもらえる力…いつしか\n全部がない交ぜになって、独りでも大丈夫だと思い込んでしまう。\n私は見誤らないわ。愛するみんなのおかげで、輝けることを。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6039, /*comment_id*/201387, /*use_type*/102, /*index*/3, /*description*/"自分の価値を決めるのは、最終的には自分なのよ。\nだから目指すところがあるなら、あえて吊り上げてみなさい。\nそのうちに、理想の姿に追いつく日がくるから。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6039, /*comment_id*/301305, /*use_type*/0, /*index*/1, /*description*/"和の心、おもてなしの心…みんなを迎える準備はバッチリ !", /*insert_word_type*/0);
