INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6039, /*comment_id*/301305, /*use_type*/102, /*index*/1, /*description*/"いっぱい悩んで考えてきたけん、成果は絶対に出るっちゃ。\nいまは自分らを信じて、まっすぐ前に進むだけ。\n深呼吸したら、走ろっ ! 思いっきり ! !", /*insert_word_type*/0);
