INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6013, /*comment_id*/300769, /*use_type*/102, /*index*/1, /*description*/"このドキドキ…部活の本番前を思い出しますね。\nちょっとの不安とそれ以上の期待で、胸の奥が熱くなって… !\nアタシ、今とっても熱い気持ちでいっぱいです !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6013, /*comment_id*/300769, /*use_type*/102, /*index*/2, /*description*/"耐えて耐えて…やっと、追い風が来ましたね !\n走り出した気持ちは、誰にも止められませんよ☆\n風より速く、前へ ! 全力で駆け抜けていきましょうっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6013, /*comment_id*/300769, /*use_type*/102, /*index*/3, /*description*/"ひとりが大変ならふたりで、ふたりでダメなら全員で !\nそうやって力を合わせれば、怖いものなんてありません。\nあとは自分の力を信じて、前へ前へ進みましょう☆", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6013, /*comment_id*/100777, /*use_type*/0, /*index*/1, /*description*/"合格への道のりは長いけど、1歩ずつ、1歩ずつ…", /*insert_word_type*/0);
