INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6038, /*comment_id*/101315, /*use_type*/101, /*index*/1, /*description*/"挑戦していきましょう。よい結果が待っているはずですわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6038, /*comment_id*/101315, /*use_type*/102, /*index*/1, /*description*/"準備をしっかりしてきた甲斐がありましたわ。\n今ならいつも以上に、物事を前へと進められそう…♪\n気持ちを整えたら、さっそく取り掛かりましょう。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6038, /*comment_id*/101315, /*use_type*/102, /*index*/2, /*description*/"ああ、完成が待ち遠しいですわ。\nけれど、焦らず丁寧に進めていって、\n誰もが笑顔になれるように頑張りますわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6038, /*comment_id*/101315, /*use_type*/102, /*index*/3, /*description*/"私、だいぶ手際がよくなってきたみたいですわ。\n思い切って挑戦してみて、本当によかった。\n私のおもてなし、どうぞ楽しみにしていてくださいませ♪", /*insert_word_type*/0);
