INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6038, /*comment_id*/301299, /*use_type*/2, /*index*/1, /*description*/"スマートにスッと渡せたら、大人っぽいよね。…できるかな？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6038, /*comment_id*/301299, /*use_type*/3, /*index*/1, /*description*/"想いを伝えたいって気持ちは、誰にも負けないから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6038, /*comment_id*/301299, /*use_type*/4, /*index*/1, /*description*/"たくさんの人が背中を押してくれた。だから、進めるんだ !", /*insert_word_type*/0);
