INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6038, /*comment_id*/301299, /*use_type*/101, /*index*/1, /*description*/"前に進まなきゃ何も変わらない…。よし、やろう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6038, /*comment_id*/301299, /*use_type*/102, /*index*/1, /*description*/"チャンス到来だね♪\nこの勢いに乗って、バンバン進むしかないでしょ☆\nさあ、この先にある可能性を目指して駆け抜けよう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6038, /*comment_id*/301299, /*use_type*/102, /*index*/2, /*description*/"いいねいいね、ノってきたね♪\n積み重ねてきた努力を信じて、ステップを踏んでいこう !\nよーし、行くぞー ! ワン、ツー、スリー♪", /*insert_word_type*/0);
