INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6036, /*comment_id*/201351, /*use_type*/102, /*index*/1, /*description*/"…あ、これ、いいアイデアかも。\nまだ思いつきだけど、意外な発想が突破口になることもあるの。\nさぁ、今すぐ動き出しましょう。時間は待ってくれないんだから。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6036, /*comment_id*/201351, /*use_type*/102, /*index*/2, /*description*/"ふふっ、やっぱり燃えてきちゃう。\n勝手知ったる仲間同士でも、勝負は勝負なんだから。\n本気で頑張りたいって思うのは、当然のことだと思わない？", /*insert_word_type*/0);
