INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6044, /*comment_id*/301341, /*use_type*/0, /*index*/1, /*description*/"道はアタシの後ろに作る ! 特攻隊長向井拓海、参上だ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6044, /*comment_id*/301341, /*use_type*/0, /*index*/2, /*description*/"静かにするとか逆に落ち着かねー…ちょっと走るか !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6044, /*comment_id*/301341, /*use_type*/0, /*index*/3, /*description*/"可愛がってくっか ! …仏恥と愛車をだよ。他にあるか？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6044, /*comment_id*/301341, /*use_type*/1, /*index*/1, /*description*/"カワイイはもういらねーからな。フリじゃねーぞ、本気だ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6044, /*comment_id*/301341, /*use_type*/2, /*index*/1, /*description*/"キメてくぜ。特攻隊長は背中で語って…クールに去るさ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6044, /*comment_id*/301341, /*use_type*/3, /*index*/1, /*description*/"出せ、声を ! 示せ、己を ! 気持ちから勝ちにいこうぜ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6044, /*comment_id*/301341, /*use_type*/4, /*index*/1, /*description*/"全部背負えたらカッケーけどな…ま、そのうちだ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6044, /*comment_id*/301341, /*use_type*/5, /*index*/1, /*description*/"誰が相手でも勝つ ! つまり、アタシが史上最強ってことだ ! ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6044, /*comment_id*/301341, /*use_type*/100, /*index*/1, /*description*/"もうすぐゴールだな ! アタシが一番乗りだ ! 飛ばすぜ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6044, /*comment_id*/301341, /*use_type*/101, /*index*/1, /*description*/"壊せない壁はねぇ。いつだって最大の敵は、自分だ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6044, /*comment_id*/301341, /*use_type*/102, /*index*/1, /*description*/"ハハッ ! エンジンかかってきたんじゃねぇか ! ？\n普通ならビビってブレーキかけちまうだろうけど、\nアタシはもっと飛ばす ! スリルも楽しもうぜ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6044, /*comment_id*/301341, /*use_type*/102, /*index*/2, /*description*/"ここまで耐えてきたご褒美ってやつか。もらっておくぜ。\n機は逃さない…アタシの後ろをついてくるヤツらのためにも。\nさ、こっからだ。切り開くぞ、道の先の道まで !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6044, /*comment_id*/301341, /*use_type*/102, /*index*/3, /*description*/"すべての速さを超えた時、たしかに生まれたんだ…興奮が。\n世界を置いていく、快感が ! 今、先頭にいるのは\nアタシなんだよ ! “亜威怒流”の天辺、掴みにいくぜ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6044, /*comment_id*/101369, /*use_type*/0, /*index*/1, /*description*/"出発進行 ! 軽快なトークのガイドは、あたしにお任せ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6044, /*comment_id*/101369, /*use_type*/0, /*index*/2, /*description*/"ワクワクするね。あたしたちらしいペースで進んでいこう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6044, /*comment_id*/101369, /*use_type*/0, /*index*/3, /*description*/"やった、最高のドライブ日和 ! 早く走りたいなー♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6044, /*comment_id*/101369, /*use_type*/1, /*index*/1, /*description*/"浮かない顔？心あったまるふたり遊び、しちゃおっか♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6044, /*comment_id*/101369, /*use_type*/2, /*index*/1, /*description*/"オッケー♪あたしのドライビングテク、見せてあげる !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6044, /*comment_id*/101369, /*use_type*/3, /*index*/1, /*description*/"ノッてきたね ! じゃあここからは、アクセル全開でっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6044, /*comment_id*/101369, /*use_type*/4, /*index*/1, /*description*/"楽しくなりそうな予感♪みんなでドライブ、楽しもう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6044, /*comment_id*/101369, /*use_type*/5, /*index*/1, /*description*/"どんな道でも大丈夫 ! あたしがちゃんとナビするからね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6044, /*comment_id*/101369, /*use_type*/100, /*index*/1, /*description*/"けっこう走ったね～ ! さ、もうすぐ目的地に到着だよっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6044, /*comment_id*/101369, /*use_type*/101, /*index*/1, /*description*/"何があっても進んでいこう ! そこに道がある限り♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6044, /*comment_id*/101369, /*use_type*/102, /*index*/1, /*description*/"まあ、調理器具だってハンドルみたいなものだし…\n大丈夫、完璧にハンドリングしてみせるよ♪\nあたしの料理テクも、たっぷり見せてあげる !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6044, /*comment_id*/101369, /*use_type*/102, /*index*/2, /*description*/"ふふっ、いい調子 ! 気分上がってきたなー♪\nこのままゴールまで、一気に駆け抜けちゃおっか？\nあ、もちろんルールは守って、安全第一で、ねっ♪\n", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6044, /*comment_id*/101369, /*use_type*/102, /*index*/3, /*description*/"肌寒い時期だからこそ、大切な人と出かけて\nたくさん笑い合って、身も心もあったまりたいな。\nあたしと一緒に、冬の思い出をつくりにいこうよ♪", /*insert_word_type*/0);
