INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6040, /*comment_id*/201395, /*use_type*/0, /*index*/1, /*description*/"自分探しの旅。今度こそ、本当の私が見つかる予感がするの", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6040, /*comment_id*/201395, /*use_type*/0, /*index*/2, /*description*/"ごまかし続けてきた、胸の奥にあるもの…見つめるときなのね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6040, /*comment_id*/201395, /*use_type*/0, /*index*/3, /*description*/"いつまでも忘れないわ。風の匂い、空の色、みんなの笑い声", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6040, /*comment_id*/201395, /*use_type*/1, /*index*/1, /*description*/"真っ白な地図に、旅の写真を貼っていくの♪ときめくわね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6040, /*comment_id*/201395, /*use_type*/2, /*index*/1, /*description*/"計画は念入りに。必ず全員、つれていくわ。私の役目だもの", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6040, /*comment_id*/201395, /*use_type*/3, /*index*/1, /*description*/"大空高く羽ばたいてみせるわ ! 私の勇気、直感、度胸でね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6040, /*comment_id*/201395, /*use_type*/4, /*index*/1, /*description*/"目を閉じれば、みんなのおしゃべりが聞こえる。力をくれるの", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6040, /*comment_id*/201395, /*use_type*/5, /*index*/1, /*description*/"どんな旅がお好みかしら？最高の5人がご案内するわ、ふふ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6040, /*comment_id*/201395, /*use_type*/100, /*index*/1, /*description*/"たとえ見知った景色でも、訪れるたびに違って見えるはずよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6040, /*comment_id*/201395, /*use_type*/101, /*index*/1, /*description*/"この旅で、何を見つめ、何を持ち帰るか…自分に問いかけるの", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6040, /*comment_id*/201395, /*use_type*/102, /*index*/1, /*description*/"聞こえてくるでしょう、風の音…旅の呼ぶ声…。\nさあ、探しに出かけましょう。\nまだ知らないあなたと私が、きっとそこにいるわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6040, /*comment_id*/201395, /*use_type*/102, /*index*/2, /*description*/"そんなに見つめないでよ…歩けなくなっちゃう…。\nべつにあなたとの旅だからって、心が逸ってるわけじゃ… !\nも、もうっ、ほら、早く行くわよっ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6040, /*comment_id*/201395, /*use_type*/102, /*index*/3, /*description*/"「ひとり旅」をしている私は、とても自由よ。でも、不思議ね。\nみんなと一緒にいると、胸の奥がざわめいて、温かくなって…。\nこの気持ちが何なのか確かめるために、私は進むわ。この道を… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6040, /*comment_id*/101333, /*use_type*/0, /*index*/1, /*description*/"慌てず焦らず、一歩一歩を大切に。積み重ねが大切ですからぁ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6040, /*comment_id*/101333, /*use_type*/0, /*index*/2, /*description*/"ときどき後ろを振り返ると、胸があったかくなるんですぅ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6040, /*comment_id*/101333, /*use_type*/0, /*index*/3, /*description*/"誰かと一緒に前に進めるって、幸せなことですよねぇ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6040, /*comment_id*/101333, /*use_type*/1, /*index*/1, /*description*/"もっと可愛く、キレイに♪クギヅケにしてあげますぅ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6040, /*comment_id*/101333, /*use_type*/2, /*index*/1, /*description*/"クールな大人メイクもお任せあれ♪キメちゃいますよぉ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6040, /*comment_id*/101333, /*use_type*/3, /*index*/1, /*description*/"よーし、張り切っていきましょう ! えいえいおーっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6040, /*comment_id*/101333, /*use_type*/4, /*index*/1, /*description*/"思い出は共有する人が多ければ多いほど、楽しいんですよぉ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6040, /*comment_id*/101333, /*use_type*/5, /*index*/1, /*description*/"仲間との絆があれば、どんなことだって乗り越えられるねぇ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6040, /*comment_id*/101333, /*use_type*/100, /*index*/1, /*description*/"仕上げまでもう少しですねぇ。最後まで気は抜けませんっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6040, /*comment_id*/101333, /*use_type*/101, /*index*/1, /*description*/"どんなに大変なことでも、仲間がいればへっちゃらですぅ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6040, /*comment_id*/101333, /*use_type*/102, /*index*/1, /*description*/"胸の奥が高鳴って、あったかい…。\n今ならもっともっと、前へ進んでいけちゃいそうですぅ !\nふふっ、これもきっと、いつかいい思い出になりそうですねぇ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6040, /*comment_id*/101333, /*use_type*/102, /*index*/2, /*description*/"昨日の私より今日の私、今日の私より明日の私のほうが\nも～っとキレイになれますよねぇ♪\n未来の素敵な自分になるために、さあ、行きましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6040, /*comment_id*/101333, /*use_type*/102, /*index*/3, /*description*/"ひとりじゃうまくいかないことも、\nみんなと一緒ならきっと乗り越えられますぅ。\n仲間との絆は、私の背中を押してくれる原動力ですからぁ♪", /*insert_word_type*/0);
