INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6025, /*comment_id*/201093, /*use_type*/0, /*index*/1, /*description*/"誰かと共に道を歩める。これほど嬉しいことはありません", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6025, /*comment_id*/201093, /*use_type*/0, /*index*/2, /*description*/"努力なくして前進なし。少しずつ積み上げて参ります", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6025, /*comment_id*/201093, /*use_type*/0, /*index*/3, /*description*/"誰かを残して進むことなどありません。一緒に行きましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6025, /*comment_id*/201093, /*use_type*/1, /*index*/1, /*description*/"疲れた時にはお酢を…え？て、天然ではありませんっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6025, /*comment_id*/201093, /*use_type*/2, /*index*/1, /*description*/"明鏡止水。揺らがず、動じず…事に臨みましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6025, /*comment_id*/201093, /*use_type*/3, /*index*/1, /*description*/"いかに困難なことであっても、楽しむ心を忘れずに… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6025, /*comment_id*/201093, /*use_type*/4, /*index*/1, /*description*/"みんなで手を取り合う。それこそが最も大切なことです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6025, /*comment_id*/201093, /*use_type*/5, /*index*/1, /*description*/"どのようなことであれ、全身全霊で臨むまでです !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6025, /*comment_id*/201093, /*use_type*/100, /*index*/1, /*description*/"勝って兜の緒を締めよ。最後まで慢心せずに行きましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6025, /*comment_id*/201093, /*use_type*/101, /*index*/1, /*description*/"私たちならきっとやれます。一意専心、励みましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6025, /*comment_id*/201093, /*use_type*/102, /*index*/1, /*description*/"ようやく機が熟しましたね。さあ、共に参りましょう !\n今まで積み上げてきたものを使って、この好機を掴み取るのです !\n大丈夫、私たちの力を信じてください !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6025, /*comment_id*/201093, /*use_type*/102, /*index*/2, /*description*/"みなさんの想いが伝わってきます…。\nこの想いに応えるためにも、立ち止まってはいられませんね。\n私たちの進むべき道は、まだまだ続いていますから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6025, /*comment_id*/201093, /*use_type*/102, /*index*/3, /*description*/"心技体の充実を感じます… !\n今こそ、大きな成果を得られる仕掛け時ですね。\nさあ、呼吸を合わせて。共に邁進しましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6025, /*comment_id*/301037, /*use_type*/0, /*index*/1, /*description*/"美味しい和菓子を目指して、一緒に頑張りましょうね～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6025, /*comment_id*/301037, /*use_type*/0, /*index*/2, /*description*/"みんなでいろいろ話し合う時間って、ワクワクします～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6025, /*comment_id*/301037, /*use_type*/0, /*index*/3, /*description*/"いつも心にゆとりを持って、のんびり進んでいきましょ～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6025, /*comment_id*/301037, /*use_type*/1, /*index*/1, /*description*/"美味しいお菓子を食べて、ふわふわ気分でいきますよ～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6025, /*comment_id*/301037, /*use_type*/2, /*index*/1, /*description*/"慌てず落ち着いて…リラックスした心が大事ですよ～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6025, /*comment_id*/301037, /*use_type*/3, /*index*/1, /*description*/"プニョフワを求めるみんなの想いに、全力で応えます～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6025, /*comment_id*/301037, /*use_type*/4, /*index*/1, /*description*/"最高のプニョフワのために、気持ちをひとつにしましょ～ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6025, /*comment_id*/301037, /*use_type*/5, /*index*/1, /*description*/"任せてください。どんな問題も、ぷにっと解決ですよ～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6025, /*comment_id*/301037, /*use_type*/100, /*index*/1, /*description*/"みんなの幸せな笑顔まで、あと少し。最後の一押しです～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6025, /*comment_id*/301037, /*use_type*/101, /*index*/1, /*description*/"みんなで力を合わせましょ～。私も精いっぱい頑張ります～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6025, /*comment_id*/301037, /*use_type*/102, /*index*/1, /*description*/"和菓子の心は、おもてなしの心……。\nつまり、みんなに笑顔になってほしいって気持ちですよね～。\nそのためなら私、いくらでも頑張れちゃいます～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6025, /*comment_id*/301037, /*use_type*/102, /*index*/2, /*description*/"うふふ、なんだか気分が乗ってきましたよ～ !\n今の私たちは絶好調、怖いものなしです～。\nプニョフワパワーで、どんどん進んじゃいましょ～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6025, /*comment_id*/301037, /*use_type*/102, /*index*/3, /*description*/"私ひとりじゃできないことも、力を合わせれば平気です～。\nだから、みんなで一緒に掴みましょうね。\nゴールで待っている、一番プニョフワな幸せを♪", /*insert_word_type*/0);
