INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6003, /*comment_id*/200533, /*use_type*/102, /*index*/1, /*description*/"この先に、どんな景色が広がっているんでしょうか……。\nあの、プロデューサーさん……\nよかったら、一緒に走って見に行きませんか？", /*insert_word_type*/0);
