INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6024, /*comment_id*/301019, /*use_type*/0, /*index*/1, /*description*/"一緒って楽しいでしょ？ゆっきーとの秋、楽しんじゃって♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6024, /*comment_id*/301019, /*use_type*/0, /*index*/2, /*description*/"秋といえばやっぱサンマ？…考えてたらお腹減ってきちゃった", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6024, /*comment_id*/301019, /*use_type*/0, /*index*/3, /*description*/"遠い目標に向かって一歩ずつ進もう。絶対やり遂げるよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6024, /*comment_id*/301019, /*use_type*/1, /*index*/1, /*description*/"ずっとそばで見ててね、あたしのこと。…え、演技の練習ー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6024, /*comment_id*/301019, /*use_type*/2, /*index*/1, /*description*/"疲れたらいつでも言って。この肩は投球のためだけじゃないよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6024, /*comment_id*/301019, /*use_type*/3, /*index*/1, /*description*/"スポーツの秋っ ! いっぱい動いた後はビールで乾杯だーっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6024, /*comment_id*/301019, /*use_type*/4, /*index*/1, /*description*/"よしっ、チームプレイだよ ! 目標に向かって…ゴー ! !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6024, /*comment_id*/301019, /*use_type*/5, /*index*/1, /*description*/"どんな壁でもかかってこーい。グラビア界の4番が相手だよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6024, /*comment_id*/301019, /*use_type*/100, /*index*/1, /*description*/"お、あともうちょっと ! 振り返ると、いろいろあったよね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6024, /*comment_id*/301019, /*use_type*/101, /*index*/1, /*description*/"かっとばせー ! しっかり狙えば、大きいの狙えるよー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6024, /*comment_id*/301019, /*use_type*/102, /*index*/1, /*description*/"イイ感じに進めて来てるんじゃない？\nこの調子で攻めていこう !\n次は大きいのが出る気がするんだよねっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6024, /*comment_id*/301019, /*use_type*/102, /*index*/2, /*description*/"楽しいことやってるとさ、もっとーって欲張りになっちゃうね。\nまだまだ終わらない秋、貪欲になっちゃってもいい？\nえへへっ…じゃ、やりたいこと全部、やり尽くしちゃおう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6024, /*comment_id*/301019, /*use_type*/102, /*index*/3, /*description*/"こうやって一緒に進むのって、なんかいいよね。\n楽しくって、幸せって感じ ! えへへっ♪\nこの先も、ずーっとあたしの隣にいてほしいんだっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6024, /*comment_id*/101041, /*use_type*/0, /*index*/1, /*description*/"私に女神のパワーがあれば…高いところの物も取れるかも !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6024, /*comment_id*/101041, /*use_type*/0, /*index*/2, /*description*/"旅には休憩も大切ですよ。甘いものでも食べましょー♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6024, /*comment_id*/101041, /*use_type*/0, /*index*/3, /*description*/"一緒にお出かけする人がいると、るんるんになりますね～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6024, /*comment_id*/101041, /*use_type*/1, /*index*/1, /*description*/"私だけの可愛さで、みんなをメロメロにしてあげますよ～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6024, /*comment_id*/101041, /*use_type*/2, /*index*/1, /*description*/"どんなことがあっても頭は冷静に…リーダーですからっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6024, /*comment_id*/101041, /*use_type*/3, /*index*/1, /*description*/"甘いものを食べてやる気いっぱいです ! やりますよぉーっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6024, /*comment_id*/101041, /*use_type*/4, /*index*/1, /*description*/"みんなの力が合わされば、きっと素敵なシーンになりますよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6024, /*comment_id*/101041, /*use_type*/5, /*index*/1, /*description*/"どんなことがあっても、お姉さんにお任せですっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6024, /*comment_id*/101041, /*use_type*/100, /*index*/1, /*description*/"あともう少しっ。最後まで気を抜かず、がんばりましょーっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6024, /*comment_id*/101041, /*use_type*/101, /*index*/1, /*description*/"きっと私たちならできます。ひとつひとつ進めましょうっ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6024, /*comment_id*/101041, /*use_type*/102, /*index*/1, /*description*/"なんだかコツが掴めちゃったかもしれませんっ !\nさあ、立ち止まってはいられませんよ。前に進みましょう !\nお姉さんの本気、見せてあげますっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6024, /*comment_id*/101041, /*use_type*/102, /*index*/2, /*description*/"みんなの期待や信頼を感じます…。\nこの絆の力を頼りに、どこまでも進んでいきましょ～っ !\nみんなと一緒なら、怖いものなしですからねっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6024, /*comment_id*/101041, /*use_type*/102, /*index*/3, /*description*/"大チャンスがやってきましたねっ。\nいっぱい積み重ねてきた分が、ここで実を結ぶんですよ !\nこの先にどんなことが待っているか、ドキドキします～♪", /*insert_word_type*/0);
