INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6031, /*comment_id*/201227, /*use_type*/0, /*index*/1, /*description*/"改めて考えるとなると悩みますよね。新年の抱負って", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6031, /*comment_id*/201227, /*use_type*/0, /*index*/2, /*description*/"歩みを止めなければ、少しずつでも近づけるんです。必ず", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6031, /*comment_id*/201227, /*use_type*/0, /*index*/3, /*description*/"悩んでいても始まりません。とにかく行動を始めましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6031, /*comment_id*/201227, /*use_type*/1, /*index*/1, /*description*/"可愛い曲だって歌いこなします。だってアイドルですから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6031, /*comment_id*/201227, /*use_type*/2, /*index*/1, /*description*/"心静かに…。そうして、自分の願いとまっすぐに向き合います", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6031, /*comment_id*/201227, /*use_type*/3, /*index*/1, /*description*/"周囲の人に元気を分けてあげられるような笑顔を、私も… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6031, /*comment_id*/201227, /*use_type*/4, /*index*/1, /*description*/"みなさんと一緒なら、私はもっと先まで進めます… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6031, /*comment_id*/201227, /*use_type*/5, /*index*/1, /*description*/"私たちはどんなアイドルにもなれる…可能性は無限大だから", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6031, /*comment_id*/201227, /*use_type*/100, /*index*/1, /*description*/"光明が見えた気がするんです。きっと、形にしてみせます", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6031, /*comment_id*/201227, /*use_type*/101, /*index*/1, /*description*/"大切なのは自分と向き合うこと…私の、この気持ちと", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6031, /*comment_id*/201227, /*use_type*/102, /*index*/1, /*description*/"大掛かりなメイクじゃなくても、いつもと違う自分に会える…\n変われないなんて思う必要はない。\n変わりたいと願うなら、思いっきり踏み出せばいいんですよね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6031, /*comment_id*/201227, /*use_type*/102, /*index*/2, /*description*/"みなさんの想い、しっかり受け取りました。\n今ならいつも以上の成果を出せる気がします !\nさぁ、新年に相応しい華やかなLIVEをしましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6031, /*comment_id*/201227, /*use_type*/102, /*index*/3, /*description*/"たくさんの言葉と、大人っぽいメイクのおかげで、\n心も体も軽くなった気分です。\n目指すべきゴールに向かって、どんどん進みます… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6031, /*comment_id*/101191, /*use_type*/0, /*index*/1, /*description*/"さあ、進みましょう ! 何はどうあれ、まずは行動あるのみ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6031, /*comment_id*/101191, /*use_type*/0, /*index*/2, /*description*/"共に歩む人がいる。これほど心強い味方はいないわね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6031, /*comment_id*/101191, /*use_type*/0, /*index*/3, /*description*/"慶次様もきっと見守ってくれてるわ。さあ、頑張りましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6031, /*comment_id*/101191, /*use_type*/1, /*index*/1, /*description*/"慶次様のことを思うと、胸が高鳴っちゃうっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6031, /*comment_id*/101191, /*use_type*/2, /*index*/1, /*description*/"どんな時でも冷静に…。大丈夫、心得てるわ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6031, /*comment_id*/101191, /*use_type*/3, /*index*/1, /*description*/"慶次様への愛は、誰にも負けないわーっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6031, /*comment_id*/101191, /*use_type*/4, /*index*/1, /*description*/"みんなで練習した成果、見せつけちゃうんだから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6031, /*comment_id*/101191, /*use_type*/5, /*index*/1, /*description*/"天下御免の傾奇者が推して参る ! なんだって来なさーい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6031, /*comment_id*/101191, /*use_type*/100, /*index*/1, /*description*/"ゴールは目前 ! さあ、一気呵成に畳みかけるのよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6031, /*comment_id*/101191, /*use_type*/101, /*index*/1, /*description*/"どんな難題でもアタシたちなら大丈夫 ! 頑張りましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6031, /*comment_id*/101191, /*use_type*/102, /*index*/1, /*description*/"好機到来ね、この時を待ってたわ !\nアタシたちならこのチャンスをものにできるはず !\nさあ、機を見て敏なり ! 一気呵成に突き進むわよ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6031, /*comment_id*/101191, /*use_type*/102, /*index*/2, /*description*/"慶次様が、アタシにもっと進めとささやいてる !\n今ならどこまでだって行けそうな気がするもの♪\nさあ、みんなで行けるところまで駆け抜けるわよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6031, /*comment_id*/101191, /*use_type*/102, /*index*/3, /*description*/"ふふっ、ワクワクが止まらないわ !\nひとりでは辿り着けない場所でも、仲間となら見られる景色が\nこの先にある ! そんな予感がアタシの背中を押してくれるの♪", /*insert_word_type*/0);
