INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6035, /*comment_id*/301259, /*use_type*/0, /*index*/1, /*description*/"一緒に暮らして、仲を深める。なろうよ、チームってやつに♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6035, /*comment_id*/301259, /*use_type*/0, /*index*/2, /*description*/"顔突き合わせて食事をすれば、仲間のこともよく見えるだろ？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6035, /*comment_id*/301259, /*use_type*/0, /*index*/3, /*description*/"ハロウィンの楽しさを伝えるには、まずウチらが楽しまなきゃ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6035, /*comment_id*/301259, /*use_type*/1, /*index*/1, /*description*/"可愛い仮装も、ハロウィンの醍醐味だよね…照れくさいけどさ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6035, /*comment_id*/301259, /*use_type*/2, /*index*/1, /*description*/"リーダーはいつも冷静に。引き受けたからにはきっちりやるよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6035, /*comment_id*/301259, /*use_type*/3, /*index*/1, /*description*/"これでもはしゃいでるんだ。ハロウィンってのは楽しいね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6035, /*comment_id*/301259, /*use_type*/4, /*index*/1, /*description*/"大所帯の面倒見るのは慣れっこさ。年季が違うよ、年季が♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6035, /*comment_id*/301259, /*use_type*/5, /*index*/1, /*description*/"どんなハロウィンがお好みだい？何でもウチにお任せあれだよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6035, /*comment_id*/301259, /*use_type*/100, /*index*/1, /*description*/"はは、この騒がしさ…実家を思い出すよ。懐かしいねぇ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6035, /*comment_id*/301259, /*use_type*/101, /*index*/1, /*description*/"みんなの笑顔のためなら、ウチはいくらでも頑張れるんだ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6035, /*comment_id*/301259, /*use_type*/102, /*index*/1, /*description*/"信じて、頼る。…言葉にすればたったこれだけなのにね。\n仲間を頼れるってことが、こんなにも安心をくれるんだって、\n今とても強く実感してる。どこまでもいける、無敵の気分だよ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6035, /*comment_id*/301259, /*use_type*/102, /*index*/2, /*description*/"互いの顔を間近で見つづけて、ウチらは心まで見せ合えた。\nきっと最高のチームになれると信じてるよ。\nLIVEだって、絶対に成功させてみせるさ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6035, /*comment_id*/301259, /*use_type*/102, /*index*/3, /*description*/"家族みたいに仲良しな、かけがえのない仲間たち。\nみんなの笑顔が、幸せがこの先にあるなら、どれだけだって\n頑張れる。それがウチの原動力だからね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6035, /*comment_id*/201329, /*use_type*/0, /*index*/1, /*description*/"ハッピーハロウィン ! 一緒にめいっぱい楽しみましょう♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6035, /*comment_id*/201329, /*use_type*/0, /*index*/2, /*description*/"お菓子はいかがですか？休憩だって、練習の一環なんですよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6035, /*comment_id*/201329, /*use_type*/0, /*index*/3, /*description*/"ひとりじゃないから、進む足は止まらない。止めないんです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6035, /*comment_id*/201329, /*use_type*/1, /*index*/1, /*description*/"ぴにゃこら太みたいに、可愛く ! ぴにゃーっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6035, /*comment_id*/201329, /*use_type*/2, /*index*/1, /*description*/"どんなことがあっても冷静に、集中して…よし、行けます !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6035, /*comment_id*/201329, /*use_type*/3, /*index*/1, /*description*/"今日まで重ねてきたレッスンこそ、私の情熱の証です !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6035, /*comment_id*/201329, /*use_type*/4, /*index*/1, /*description*/"みんなと一緒なら、どんな壁だって乗り越えられますよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6035, /*comment_id*/201329, /*use_type*/5, /*index*/1, /*description*/"楽しみましょう。どんな曲でも、心のままに♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6035, /*comment_id*/201329, /*use_type*/100, /*index*/1, /*description*/"いよいよゴールですね。集中を切らさず、駆け抜けましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6035, /*comment_id*/201329, /*use_type*/101, /*index*/1, /*description*/"ふふっ、ワクワクしますね。挑戦って、楽しいです♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6035, /*comment_id*/201329, /*use_type*/102, /*index*/1, /*description*/"ふふっ、ワクワクが止まりません…♪\n今ならどこまでも行けそうな気がするんです !\nアレグロよりも素早く、さあ、行きましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6035, /*comment_id*/201329, /*use_type*/102, /*index*/2, /*description*/"これまでの流れから…予感がするんです。\nチャンスは今、目の前にあるんだって。\n積み重ねてきた練習と経験を信じて、ものにしてみせますね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6035, /*comment_id*/201329, /*use_type*/102, /*index*/3, /*description*/"これまでの歩みは、私ひとりだけの力じゃありません。\n一緒に歩んでくれる、みんなの力があってこそのものです。\n掴みましょう。みんなで一緒に、ゴールの先にあるものを !", /*insert_word_type*/0);
