INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6034, /*comment_id*/101259, /*use_type*/5, /*index*/1, /*description*/"見るもの、聞くもの、触れるもの……全てが新鮮な体験ですの", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6034, /*comment_id*/101259, /*use_type*/100, /*index*/1, /*description*/"あと少しですわね。この想いが、旋律へと変わるまで…… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6034, /*comment_id*/101259, /*use_type*/101, /*index*/1, /*description*/"信じております。ここでの経験がわたくしを成長させるのだと", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6034, /*comment_id*/101259, /*use_type*/102, /*index*/1, /*description*/"わたくしが目にしたもの、耳にした音、得た美しき経験の全てを、\n演奏に乗せて響かせたいのです。参りましょう。\nこの旋律を、より多くの人へ届けるために !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6034, /*comment_id*/101259, /*use_type*/102, /*index*/2, /*description*/"多くの出会いを経験すればこそ、紡げる音があるのです。\nならば今こそが、わたくしの演奏に、より深みを出せる\nチャンスなのでしょう。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6034, /*comment_id*/101259, /*use_type*/102, /*index*/3, /*description*/"わたくしたちはもう知っていますわ。音楽には力があると。\n人の背を押し、夢への道行きを照らし……\n険しい道も、颯爽と歩かせる力が。", /*insert_word_type*/0);
