INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6026, /*comment_id*/101081, /*use_type*/0, /*index*/1, /*description*/"誰かと一緒というのは、心強くもあり、楽しいものですね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6026, /*comment_id*/101081, /*use_type*/0, /*index*/2, /*description*/"今日はどんなシャッターチャンスが待っているでしょう？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6026, /*comment_id*/101081, /*use_type*/0, /*index*/3, /*description*/"努力も思い出も、積み重なるほど素敵ですよね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6026, /*comment_id*/101081, /*use_type*/1, /*index*/1, /*description*/"ベストショットが撮れると、やはり気分がいいものですね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6026, /*comment_id*/101081, /*use_type*/2, /*index*/1, /*description*/"焦らず、逸らず…冷静に待ち続ければ、チャンスは訪れます", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6026, /*comment_id*/101081, /*use_type*/3, /*index*/1, /*description*/"カメラにかける情熱は、誰にも負けませんっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6026, /*comment_id*/101081, /*use_type*/4, /*index*/1, /*description*/"最高の一枚を撮るために、みなさんと心をひとつに… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6026, /*comment_id*/101081, /*use_type*/5, /*index*/1, /*description*/"振り返れば、多くの思い出…。挑みましょう、自信を持って !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6026, /*comment_id*/101081, /*use_type*/100, /*index*/1, /*description*/"あともう少しですね。最後まで気を緩めずにいきましょう♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6026, /*comment_id*/101081, /*use_type*/101, /*index*/1, /*description*/"みんなで力を合わせれば、越えられないものはありませんよ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6026, /*comment_id*/101081, /*use_type*/102, /*index*/1, /*description*/"今がシャッターチャンスですね♪\nつぼみの時代に蓄え続けてきたものを今、花開かせましょう。\nこの一瞬に、全力で咲き誇ってみせます。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6026, /*comment_id*/101081, /*use_type*/102, /*index*/2, /*description*/"私、今がすごく楽しいんです。\n見たもの、感じたもの、積み重ねてきたもの全部が、\n私にとってかけがえのない宝物です♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6026, /*comment_id*/101081, /*use_type*/102, /*index*/3, /*description*/"この先には、どんな景色が待っているんでしょう？\n素敵な写真が撮れそうで、ドキドキしてしまいますね♪\nさあ、一緒に見に行ってみましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6026, /*comment_id*/201113, /*use_type*/0, /*index*/1, /*description*/"風の向くまま、気の向くまま。焦らずゆっくり進みましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6026, /*comment_id*/201113, /*use_type*/0, /*index*/2, /*description*/"同行する仲間との他愛ないお喋りも、大事な旅の思い出よ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6026, /*comment_id*/201113, /*use_type*/0, /*index*/3, /*description*/"貴方自身の目的も達成できるように、お手伝いさせて", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6026, /*comment_id*/201113, /*use_type*/1, /*index*/1, /*description*/"旅先での愛らしい動物との出会い。そんな楽しみも旅の定番ね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6026, /*comment_id*/201113, /*use_type*/2, /*index*/1, /*description*/"世界を巡って磨いたセンス…アイドルとしての武器を見せるわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6026, /*comment_id*/201113, /*use_type*/3, /*index*/1, /*description*/"お祭り時期の旅行もいいわよ。熱気に身を任せましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6026, /*comment_id*/201113, /*use_type*/4, /*index*/1, /*description*/"旅は一種の非日常。いろいろ欲張りたくなる気持ちはわかるわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6026, /*comment_id*/201113, /*use_type*/5, /*index*/1, /*description*/"直感で行き先を決めてみるのも、いい気分転換になるわよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6026, /*comment_id*/201113, /*use_type*/100, /*index*/1, /*description*/"目的地が近づく高揚感は、どこに行っても変わらないわね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6026, /*comment_id*/201113, /*use_type*/101, /*index*/1, /*description*/"旅の途中にトラブルはつきもの。大丈夫、なるようになるわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6026, /*comment_id*/201113, /*use_type*/102, /*index*/1, /*description*/"みんなと旅を共にして、私も刺激をもらったわ。\n今日はいつもより遠くまで足を運べそう。\n貴方も、遅れないようについてきてね。ふふっ…♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6026, /*comment_id*/201113, /*use_type*/102, /*index*/2, /*description*/"人生という旅は、いつだって未確定。\nだからこそ私たちはずっと歩き続けていられるのね。\nさぁ、貴方も一緒に、新たな出会いに向けて進みましょう。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6026, /*comment_id*/201113, /*use_type*/102, /*index*/3, /*description*/"不思議ね。春のせいか、心が浮き足立っているのを感じるわ。\nこんな日はいつもと違う出会いが待っていそうよ。\nだから…心のままに、気楽に一歩、踏み出してみましょうか。", /*insert_word_type*/0);
