INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6002, /*comment_id*/300495, /*use_type*/100, /*index*/1, /*description*/"もうちょっとでゴールだね ! 気合い入れなおしてこ !", /*insert_word_type*/0);
