INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6002, /*comment_id*/300495, /*use_type*/102, /*index*/1, /*description*/"トロピカルジュースでリフレッシュしたし\nいつもよりアツい踊りができそう !\nさ、盛り上げていくよーっ ! !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6002, /*comment_id*/300495, /*use_type*/102, /*index*/2, /*description*/"ダンスバカの本気、\n見ててよ、プロデューサー !\nバリのみんなと一緒にすごいステージにするからさ !", /*insert_word_type*/0);
