INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6037, /*comment_id*/301293, /*use_type*/0, /*index*/1, /*description*/"スペシャルギャグで、鮮やかな笑いをおこしますよ～ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6037, /*comment_id*/301293, /*use_type*/0, /*index*/2, /*description*/"芸術ロケはやっぱり、勢いこそが正義… ! 豪快にいきます !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6037, /*comment_id*/301293, /*use_type*/0, /*index*/3, /*description*/"小さなギャグも、積み重ねていくのが大事なんです。えへん !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6037, /*comment_id*/301293, /*use_type*/1, /*index*/1, /*description*/"キュートな笑顔にきゅー、っとくる ! えへへっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6037, /*comment_id*/301293, /*use_type*/2, /*index*/1, /*description*/"クール…クールギャグ…。あっ、あと一分待ってください～ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6037, /*comment_id*/301293, /*use_type*/3, /*index*/1, /*description*/"寒いですか？パッション矢口にお任せあれ ! あったまれ～ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6037, /*comment_id*/301293, /*use_type*/4, /*index*/1, /*description*/"可愛い微笑みも面白ギャグも、アイドルの武器ですから♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6037, /*comment_id*/301293, /*use_type*/5, /*index*/1, /*description*/"一家にひとつ、365日矢口の笑顔カレンダー、どうぞ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6037, /*comment_id*/301293, /*use_type*/100, /*index*/1, /*description*/"ゴールまであとちょっとですねっ ! ふぁいとー ! えいおー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6037, /*comment_id*/301293, /*use_type*/101, /*index*/1, /*description*/"大変なときこそ笑顔 ! 息抜きにひと笑いしましょう♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6037, /*comment_id*/301293, /*use_type*/102, /*index*/1, /*description*/"わたし、楽しむことの天才だと我ながら思うんですよね !\nどんな大変なことにだって、面白さを見つけられちゃうんです。\n今だって、すっごく楽しい ! パワーも倍です !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6037, /*comment_id*/301293, /*use_type*/102, /*index*/2, /*description*/"おおお… ! 速い…わたし、風になってる ! ？\nよし ! 進むしかないですね、この勢いでドドーンと !\nみんなを笑わせながら、わたしがてっぺんとっちゃいます !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6037, /*comment_id*/301293, /*use_type*/102, /*index*/3, /*description*/"矢口、スピードアーップ !\nわたしの成長は、とどまるところを知りませんね !\nいつか、わたしがいるだけでみんなが笑顔になるかも !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6037, /*comment_id*/201363, /*use_type*/0, /*index*/1, /*description*/"どんなときも、楽しむ心は忘れずに。私も胸に刻まなきゃね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6037, /*comment_id*/201363, /*use_type*/0, /*index*/2, /*description*/"思いきっていきましょう。だんだん進み方もわかってくるはず", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6037, /*comment_id*/201363, /*use_type*/0, /*index*/3, /*description*/"真っすぐな道でも曲がりくねった道でも同じ。前に進むだけよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6037, /*comment_id*/201363, /*use_type*/1, /*index*/1, /*description*/"私も可愛く子蜘蛛になりきって…いえ、なんでもないわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6037, /*comment_id*/201363, /*use_type*/2, /*index*/1, /*description*/"集中すれば、進むべき道は見えてくるの。そう、自然とね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6037, /*comment_id*/201363, /*use_type*/3, /*index*/1, /*description*/"ときには勢いも大事なのよ？ほら、一緒に進みましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6037, /*comment_id*/201363, /*use_type*/4, /*index*/1, /*description*/"みんなで遊びましょう。見てるだけじゃもったいないわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6037, /*comment_id*/201363, /*use_type*/5, /*index*/1, /*description*/"任せてちょうだい。どんな道だって、私が案内してあげる", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6037, /*comment_id*/201363, /*use_type*/100, /*index*/1, /*description*/"あら、ゴールの光が…でも、気を抜くにはまだ早いわね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6037, /*comment_id*/201363, /*use_type*/101, /*index*/1, /*description*/"私は、挑戦している自分が好きよ。貴方も同じでしょう？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6037, /*comment_id*/201363, /*use_type*/102, /*index*/1, /*description*/"ふふ…楽しくて、つい夢中になっちゃった。\nたまには、こんなふうに無邪気に遊ぶのも悪くないわよね。\nさあ、この調子で、一気に進んじゃいましょうか。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6037, /*comment_id*/201363, /*use_type*/102, /*index*/2, /*description*/"蜘蛛の歩き方も、すっかり板についてきたんじゃない？\n遊びながら役作りなんて、面白いわよね。\nこのまま最速目指して、頑張っちゃおうかしら♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6037, /*comment_id*/201363, /*use_type*/102, /*index*/3, /*description*/"今が稽古の成果を見せるときのようね。\n一度狙いを定めたら、蜘蛛のように素早く這いよって…\nこのチャンス、絶対にものにしましょう !", /*insert_word_type*/0);
