INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6020, /*comment_id*/300937, /*use_type*/5, /*index*/1, /*description*/"いろんな磨き方して、理想のあたしへ…向かっていきます !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6020, /*comment_id*/300937, /*use_type*/100, /*index*/1, /*description*/"あと少しっ ! 頑張った成果が、向こうで待ってますよぉ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6020, /*comment_id*/300937, /*use_type*/101, /*index*/1, /*description*/"壁を越えたら、理想に一歩近づける…よし、気合十分です♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6020, /*comment_id*/300937, /*use_type*/102, /*index*/1, /*description*/"なんかすっごくイイ感じになってきたかもぉ !\nここで立ち止まっちゃうのはもったいないですよぉ♪\n溜めてきた分、出したい時に出し切っちゃおー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6020, /*comment_id*/300937, /*use_type*/102, /*index*/2, /*description*/"アイドルとプロデューサーの関係って、磨き合ってるって感じ。\n二人三脚で、お互いの魅力を高め合ってますよねぇ♪\nそう、一緒だからあたし、どこまでも上へ行けちゃうんですっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6020, /*comment_id*/300937, /*use_type*/102, /*index*/3, /*description*/"あたしの理想は、まだまだ遠いところにあるんです。\nでも、焦ったらもっと遠くにいっちゃうから、ゆっくり確実に。\nそしたら、きっとチャンスも見えてきますよねぇ♪ほらっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6020, /*comment_id*/200973, /*use_type*/0, /*index*/1, /*description*/"誰にでももう一度チャンスは訪れる…私にだって来たんだから", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6020, /*comment_id*/200973, /*use_type*/0, /*index*/2, /*description*/"常に自分を磨いて、いつでも最高の状態にしていくわ… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6020, /*comment_id*/200973, /*use_type*/0, /*index*/3, /*description*/"褒められたらむしろ気を引き締めるの。慢心しないように", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6020, /*comment_id*/200973, /*use_type*/1, /*index*/1, /*description*/"可愛い…って難しいわ。もちろん諦めたりはしないけどね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6020, /*comment_id*/200973, /*use_type*/2, /*index*/1, /*description*/"常に顎を上げて、凛と。下を向かないのが私の矜持よ", /*insert_word_type*/0);
