INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6020, /*comment_id*/200973, /*use_type*/4, /*index*/1, /*description*/"いろんな人に助けられているんだって、最近特にそう感じるの", /*insert_word_type*/0);
