INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6020, /*comment_id*/200973, /*use_type*/100, /*index*/1, /*description*/"ゴールに向かうまでの道を楽しみましょう。自分だけの歩みを", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6020, /*comment_id*/200973, /*use_type*/101, /*index*/1, /*description*/"挑戦ができることの喜びを、行動で示してみせるわ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6020, /*comment_id*/200973, /*use_type*/102, /*index*/1, /*description*/"気分が乗ってきたのかしら？いい調子よ。\nこの勢いのまま、駆け抜けてしまいましょう。\n多少荒くなっても、後から見たらそれが味になっているわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6020, /*comment_id*/200973, /*use_type*/102, /*index*/2, /*description*/"迷わない、前だけを向く…それが私にできること。\nだけど、貴方が横にいてくれるなら…ほんのときどき、\n寄りかかってもいい？そのあとは、倍以上の力で進むから。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6020, /*comment_id*/200973, /*use_type*/102, /*index*/3, /*description*/"前は「やらなきゃいけない」…それにがんじがらめ。\nでも今は「やりたい」の。楽しいことも辛いことも、なんでも。\nどんなこともワクワクするわ。だから…ついてきてね。", /*insert_word_type*/0);
