INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6019, /*comment_id*/100919, /*use_type*/0, /*index*/1, /*description*/"いろんな小春を見せられるように、頑張りますよ～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6019, /*comment_id*/100919, /*use_type*/0, /*index*/2, /*description*/"お空を飛べたら～、鳥さんとお散歩したいです～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6019, /*comment_id*/100919, /*use_type*/0, /*index*/3, /*description*/"魔法をかけちゃいます～。小春を絶対忘れない魔法～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6019, /*comment_id*/100919, /*use_type*/1, /*index*/1, /*description*/"小悪魔でも、お姫様に大事なのは可愛さですよね～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6019, /*comment_id*/100919, /*use_type*/2, /*index*/1, /*description*/"かっこよく、ビシ～っときめます～。笑顔もクールに～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6019, /*comment_id*/100919, /*use_type*/3, /*index*/1, /*description*/"素敵なお姫様を目指して、元気いっぱいにいきますよ～ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6019, /*comment_id*/100919, /*use_type*/4, /*index*/1, /*description*/"みんなが一緒だから、小春は迷子にならないで進めます～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6019, /*comment_id*/100919, /*use_type*/5, /*index*/1, /*description*/"どの曲でも平気です～。しっかりと歌ってみせますよ～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6019, /*comment_id*/100919, /*use_type*/100, /*index*/1, /*description*/"わぁ、もう少し～。早くゴールができる魔法をかけますよ～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6019, /*comment_id*/100919, /*use_type*/101, /*index*/1, /*description*/"きっと大丈夫です。小春の頑張り、見ていてくださいね～", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6019, /*comment_id*/100919, /*use_type*/102, /*index*/1, /*description*/"今、ビビッときました～。\n力をためて、たくさん進みましょう～ !\nえへへ～、どこまで行けるのか楽しみです～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6019, /*comment_id*/100919, /*use_type*/102, /*index*/2, /*description*/"小春、今すごくお姫様っぽくなってる気がします～ !\n特訓の成果かな～？\nこの嬉しい気持ちを、お裾分けしちゃいます～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6019, /*comment_id*/100919, /*use_type*/102, /*index*/3, /*description*/"コツがわかってきました～。\nきっと今なら、ずっと先まで行けます～。\nこの先に何が待っているのか…考えただけでワクワクです～♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6019, /*comment_id*/300915, /*use_type*/0, /*index*/1, /*description*/"まずはしっかりストレッチ ! 体も心も準備しようっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6019, /*comment_id*/300915, /*use_type*/0, /*index*/2, /*description*/"んー…役の心になりきる、か…難しいけど、頑張らなきゃ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6019, /*comment_id*/300915, /*use_type*/0, /*index*/3, /*description*/"ゴールへ向かっていくのって、やっぱワクワクするよっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6019, /*comment_id*/300915, /*use_type*/1, /*index*/1, /*description*/"可愛いってよくわかんないけど、笑顔は得意だよ ! にーっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6019, /*comment_id*/300915, /*use_type*/2, /*index*/1, /*description*/"クールなのも任せて。…キリッ ! どう？結構イケるでしょ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6019, /*comment_id*/300915, /*use_type*/3, /*index*/1, /*description*/"全力パワーで、まひろダッシュ ! エナジーは笑顔だよっ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6019, /*comment_id*/300915, /*use_type*/4, /*index*/1, /*description*/"みんな、レーンに入って。さぁ、一緒に走っていこうっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6019, /*comment_id*/300915, /*use_type*/5, /*index*/1, /*description*/"どんな曲でもOKだよ ! 私のこと信じて、前に進もう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6019, /*comment_id*/300915, /*use_type*/100, /*index*/1, /*description*/"あと少し… ! 大事な気持ちを胸に、ゴールテープを切ろう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6019, /*comment_id*/300915, /*use_type*/101, /*index*/1, /*description*/"壁の高さなんて関係ない…どんなのだって越えてみせるから !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6019, /*comment_id*/300915, /*use_type*/102, /*index*/1, /*description*/"心が高鳴ってきたら、もう止まれないよね !\nランナーズハイって感じ !\nこのまま、行けるとこまで行ってみようよっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6019, /*comment_id*/300915, /*use_type*/102, /*index*/2, /*description*/"ひとりで走って見る景色もいいけど、ふたりでならもっといい !\n一緒に進んでるからかな、体も軽くてさっ♪\nそばにいると、いつでも追い風を感じられるみたい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6019, /*comment_id*/300915, /*use_type*/102, /*index*/3, /*description*/"難しかったこと、悩んだこと、全部全部バネにして、\n前よりずっと先へ進める…そんな気がするんだ。\nだってもう、私の心には「好き」しかないんだもんっ !", /*insert_word_type*/0);
