INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6023, /*comment_id*/101017, /*use_type*/0, /*index*/1, /*description*/"この先に新しい発見や発想があるかと思うと、胸が躍るな !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6023, /*comment_id*/101017, /*use_type*/0, /*index*/2, /*description*/"着実に積み重ねよう。天才でも積み重ねは大事だからな", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6023, /*comment_id*/101017, /*use_type*/0, /*index*/3, /*description*/"ふふ、誰かと歩くのも悪くない気分だ。さあ、共に行こう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6023, /*comment_id*/101017, /*use_type*/1, /*index*/1, /*description*/"ロボのデザインは可愛さも重要だな。親しみやすくなる !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6023, /*comment_id*/101017, /*use_type*/2, /*index*/1, /*description*/"冷静かつ正確なロボ作りにこそ、魂が宿るというものだ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6023, /*comment_id*/101017, /*use_type*/3, /*index*/1, /*description*/"私はまだまだ元気だぞ。情熱では負けていられないからな", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6023, /*comment_id*/101017, /*use_type*/4, /*index*/1, /*description*/"仲間と力を合わせれば、どんなことだってできるはずだ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6023, /*comment_id*/101017, /*use_type*/5, /*index*/1, /*description*/"なんでも来るがいい。この天才に不可能などありはしない !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6023, /*comment_id*/101017, /*use_type*/100, /*index*/1, /*description*/"あともう一息だな。最後の最後まで抜かりなく行くぞ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6023, /*comment_id*/101017, /*use_type*/101, /*index*/1, /*description*/"私は天才だからな。どんな課題でもクリアしてみせよう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6023, /*comment_id*/101017, /*use_type*/102, /*index*/1, /*description*/"我々にもチャンスが巡ってきたな ! この機を逃す手はないぞ !\n助手よ、もちろん準備はできているだろうな？\n立ち止まらず、一気に駆け抜けようではないか !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6023, /*comment_id*/101017, /*use_type*/102, /*index*/2, /*description*/"みんなと協力すれば困難などあろうはずもない。\nひとりでは行けなかった場所まで、どこまでも進めそうだ !\nさあ、堂々と胸を張って共に進もう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6023, /*comment_id*/101017, /*use_type*/102, /*index*/3, /*description*/"この先に一体何があるのか…フフ、好奇心を抑えきれんな !\n行けるところまで行ってみようではないか。\nしっかりと付いて来るのだぞ、助手よ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6023, /*comment_id*/201049, /*use_type*/0, /*index*/1, /*description*/"私も、魔法が使えたらいいな…。動物さんと、お話したり…♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6023, /*comment_id*/201049, /*use_type*/0, /*index*/2, /*description*/"旅のお供は…歌ですよね…。一緒に、声を合わせましょう…♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6023, /*comment_id*/201049, /*use_type*/0, /*index*/3, /*description*/"ちょっとずつでも、進んでるはず…。だから、大丈夫です", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6023, /*comment_id*/201049, /*use_type*/1, /*index*/1, /*description*/"とんがり帽子…被れるの、楽しみにしてて…。内緒ですよ…？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6023, /*comment_id*/201049, /*use_type*/2, /*index*/1, /*description*/"雪が降る日は…静かで、落ち着いて…少しだけ、寂しいです…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6023, /*comment_id*/201049, /*use_type*/3, /*index*/1, /*description*/"ダンスも、元気よく… ! 小さい子にも楽しんでほしいから…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6023, /*comment_id*/201049, /*use_type*/4, /*index*/1, /*description*/"歌に踊りに、演技も…どれから練習していこうかな…？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6023, /*comment_id*/201049, /*use_type*/5, /*index*/1, /*description*/"やることはたくさん…。ひとつずつ、クリアしていきたいです", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6023, /*comment_id*/201049, /*use_type*/100, /*index*/1, /*description*/"あと少し… ! 今のうちに、復習とか…しておこうかな…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6023, /*comment_id*/201049, /*use_type*/101, /*index*/1, /*description*/"緊張します…。でも…みんなで、がんばってきたから… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6023, /*comment_id*/201049, /*use_type*/102, /*index*/1, /*description*/"ひとりでは、怖い場所も…みんなと力を合わせたら、\n楽しみながら、どこまでも進んでいけるんです…。\nだから、あなたも一緒に…私と、行きましょう…♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6023, /*comment_id*/201049, /*use_type*/102, /*index*/2, /*description*/"旅って…きっと、楽しいことも、不安なこともあります…。\nでもそれは、踏み出してみないと…わからないことだから…。\n最初の一歩…怖がらないで、踏み出してみます… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6023, /*comment_id*/201049, /*use_type*/102, /*index*/3, /*description*/"わからないことが…わかるようになって…目の前が開けて…。\nそんな気持ちを…アイドルになってから、ずっと感じてます…。\n次は…どんな私が、見つかるのかな…ふふっ…♪", /*insert_word_type*/0);
