INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6004, /*comment_id*/200557, /*use_type*/102, /*index*/1, /*description*/"思いっきり遊んで充電完了よ。\nなにごとも、メリハリが大事よね。\nさぁ、スピードを上げて、一気にいきましょう。", /*insert_word_type*/0);
