INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6028, /*comment_id*/201155, /*use_type*/0, /*index*/1, /*description*/"大自然に囲まれると気分も晴れやか、いい体験ができますね♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6028, /*comment_id*/201155, /*use_type*/0, /*index*/2, /*description*/"田舎の暑さは、東京の暑さと全然違って。湿度のせいかな？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6028, /*comment_id*/201155, /*use_type*/0, /*index*/3, /*description*/"木々のざわめきに耳を傾けると、とっても落ち着くんですよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6028, /*comment_id*/201155, /*use_type*/1, /*index*/1, /*description*/"可愛い水着を選んだつもりなんですけれど…どうでしょうか？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6028, /*comment_id*/201155, /*use_type*/2, /*index*/1, /*description*/"水辺で穏やかに微笑んでいたら、私も大人な雰囲気に…？", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6028, /*comment_id*/201155, /*use_type*/3, /*index*/1, /*description*/"野山を目の前にすると、心の中に湧き上がる冒険心が… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6028, /*comment_id*/201155, /*use_type*/4, /*index*/1, /*description*/"都会から少し離れるだけで、素敵な自然がたくさんです♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6028, /*comment_id*/201155, /*use_type*/5, /*index*/1, /*description*/"ここで体験することの全てが、私にとっての冒険なんです !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6028, /*comment_id*/201155, /*use_type*/100, /*index*/1, /*description*/"なにか掴めそう…キラキラの宝物を発見する前触れのような…", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6028, /*comment_id*/201155, /*use_type*/101, /*index*/1, /*description*/"謎解きは冒険にはつきものです。正念場ですよ… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6028, /*comment_id*/201155, /*use_type*/102, /*index*/1, /*description*/"私の探している宝物……私の求める冒険……。\nそれが一体何なのかを、思い出せた気がします。\nさあ、かけがえのないキラキラを、一緒に見つけましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6028, /*comment_id*/201155, /*use_type*/102, /*index*/2, /*description*/"心躍る冒険と出会えるかどうかは、どこへ行くか…よりも\n誰と行くか…の方が大切なのかもしれません。\nみなさんといっしょなら、それこそどこでだって !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6028, /*comment_id*/201155, /*use_type*/102, /*index*/3, /*description*/"心の中の宝の地図…全貌が見えました！\nこの先もずっと記憶の中に残り続けるとびきりの宝物を、\nこの手に掴み取りに行きましょう !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6028, /*comment_id*/301101, /*use_type*/0, /*index*/1, /*description*/"今日はいい波に乗れる予感 ! 繰り出せ海 ! いくわよー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6028, /*comment_id*/301101, /*use_type*/0, /*index*/2, /*description*/"やりたいと思ったらやる。戸惑ってたら時間がなくなるわ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6028, /*comment_id*/301101, /*use_type*/0, /*index*/3, /*description*/"疲れることなんて考えない。楽しんでたらそんな暇ないもの♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6028, /*comment_id*/301101, /*use_type*/1, /*index*/1, /*description*/"やーん、うふふ☆…やば、最近のカワイイ…わかんないかも !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6028, /*comment_id*/301101, /*use_type*/2, /*index*/1, /*description*/"息抜きは終わり。真面目にこなすわ。でも楽しさは忘れずに !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6028, /*comment_id*/301101, /*use_type*/3, /*index*/1, /*description*/"ガンガンいって ! 後ろ振り返る余裕なんて残さないわ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6028, /*comment_id*/301101, /*use_type*/4, /*index*/1, /*description*/"水を得た魚って感じかしら？いつだって今が全盛期よ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6028, /*comment_id*/301101, /*use_type*/5, /*index*/1, /*description*/"苦しい時は海に向かって歌うの。いいストレス発散よ♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6028, /*comment_id*/301101, /*use_type*/100, /*index*/1, /*description*/"もうちょっとよ ! 駆け抜けましょう ! 全部出し切って !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6028, /*comment_id*/301101, /*use_type*/101, /*index*/1, /*description*/"障害は高いほど燃えるでしょ。波と一緒♪乗り越えてこそよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6028, /*comment_id*/301101, /*use_type*/102, /*index*/1, /*description*/"ゴールデンタイムってやつ、きたんじゃない ! ？\nこのビッグウェーブは乗るしかないでしょ !\nほら ! 何してるの、ぼーっとしてないで走った走った !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6028, /*comment_id*/301101, /*use_type*/102, /*index*/2, /*description*/"チャンス到来ね ! ここをものにしないでいつするの？\n思い切って前に進めば、その分たくさんのものをつかみ取れるわ。\nふふっ、これはお姉さんの経験則♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6028, /*comment_id*/301101, /*use_type*/102, /*index*/3, /*description*/"今ならなんでもできるかも、って思う時ってあるじゃない？\n突然ピンとくるっていうか、視界が開けるっていうか？\nそれがまさにきてる。この感覚が薄れないうちにやりますか !", /*insert_word_type*/0);
