INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6029, /*comment_id*/201177, /*use_type*/100, /*index*/1, /*description*/"あと一歩ね。さあ、もっと気を引き締めていきましょう", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6029, /*comment_id*/201177, /*use_type*/101, /*index*/1, /*description*/"大丈夫。自分自身を…積み重ねてきた努力を、信じるだけよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6029, /*comment_id*/201177, /*use_type*/102, /*index*/1, /*description*/"華やかな空気に気後れしてしまいそうになるけれど…\n俯いていたら、楽しいことだって見逃してしまう。\n顔を上げて進みましょう。私が、貴方を案内するわ。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6029, /*comment_id*/201177, /*use_type*/102, /*index*/2, /*description*/"たまには、知らない場所に足を運んでみるのもいいわよね。\n新しい刺激や、思いもよらない自分と出会えて…\n今よりも、少しだけ世界が広がるかもしれないもの。", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6029, /*comment_id*/201177, /*use_type*/102, /*index*/3, /*description*/"ワクワクしてる？ふふ…もちろん、私もよ。 \nこの先、どんなことが待っているのかしら？\n予想がつかないけれど…だからこそ、こんなに最高の気分なの。", /*insert_word_type*/0);
