INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6029, /*comment_id*/101149, /*use_type*/102, /*index*/1, /*description*/"ドーナツパワーが高まっていくのを感じるよ～ !\n今ならどこまでも行ける気がする !\nみんなで行けるところまで行っちゃおー !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6029, /*comment_id*/101149, /*use_type*/102, /*index*/2, /*description*/"みんなの大好きが伝わってくる…。\n集まった想いの力が、あたしたちの背中を押してくれてる !\nえへへっ、今ならいくらでも頑張れちゃいそうだよ～ !", /*insert_word_type*/0);
