INSERT INTO `rail_card_comment` VALUES (/*id*/1, /*event_id*/6042, /*comment_id*/101351, /*use_type*/0, /*index*/1, /*description*/"1に努力、2に努力。ずっとアタシの取り柄で、こだわりなんだ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/2, /*event_id*/6042, /*comment_id*/101351, /*use_type*/0, /*index*/2, /*description*/"今、目指すのはトップアイドル。この道の先に繋がってるよね", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/3, /*event_id*/6042, /*comment_id*/101351, /*use_type*/0, /*index*/3, /*description*/"アタシたちの夢を笑う人がいたら、一緒に見返してやろうっ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/4, /*event_id*/6042, /*comment_id*/101351, /*use_type*/1, /*index*/1, /*description*/"小さい頃に憧れたのは、やっぱり可愛いアイドルなんだよねっ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/5, /*event_id*/6042, /*comment_id*/101351, /*use_type*/2, /*index*/1, /*description*/"分析はバッチリ ! キマってるでしょ、これが都会のセンス♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/6, /*event_id*/6042, /*comment_id*/101351, /*use_type*/3, /*index*/1, /*description*/"情熱なら負けないよ。もちろん覚悟もね。全部、出し切るっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/7, /*event_id*/6042, /*comment_id*/101351, /*use_type*/4, /*index*/1, /*description*/"最初は独りぼっちだったな。でも今は味方がたくさんいるんだ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/8, /*event_id*/6042, /*comment_id*/101351, /*use_type*/5, /*index*/1, /*description*/"見せつけよう ! アイドル忍はなんでもやるってとこをさっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/9, /*event_id*/6042, /*comment_id*/101351, /*use_type*/100, /*index*/1, /*description*/"あと1歩…1歩がたりなくて、何度も泣いてきた。でも今は… !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/10, /*event_id*/6042, /*comment_id*/101351, /*use_type*/101, /*index*/1, /*description*/"挑戦、できるんだよね。今アタシ、楽しくてしかたないっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/11, /*event_id*/6042, /*comment_id*/101351, /*use_type*/102, /*index*/1, /*description*/"疲れなんて忘れてさ、ついついやりすぎちゃうんだ。\nでも、言ってほしいな、「もっと行こう」って。\nこの体の熱、今は冷ましたくないっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/12, /*event_id*/6042, /*comment_id*/101351, /*use_type*/102, /*index*/2, /*description*/"出たっ ! ボーナス2倍のおまけつき !\nこういうの、しっかり活用しなきゃね。\n上京したての頃に覚えた、生活の知恵 ! へへ～ん♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/13, /*event_id*/6042, /*comment_id*/101351, /*use_type*/102, /*index*/3, /*description*/"走りつづけるのは、自分のため。それからもうひとつ、\n大切な理由があるよ。つれていきたいんだ、\nアタシのプロデューサーさんを、いちばん高い景色までさっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/14, /*event_id*/6042, /*comment_id*/301323, /*use_type*/0, /*index*/1, /*description*/"少しずつでもよかばい。楽しみながら、進んでいくと !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/15, /*event_id*/6042, /*comment_id*/301323, /*use_type*/0, /*index*/2, /*description*/"ためらってたらもったいなか。いつでも前を向いていくばい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/16, /*event_id*/6042, /*comment_id*/301323, /*use_type*/0, /*index*/3, /*description*/"迷ったら、頼ってくれてよかと。ウチに任せときんしゃい♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/17, /*event_id*/6042, /*comment_id*/301323, /*use_type*/1, /*index*/1, /*description*/"あいらしか格好、なかなか慣れんけど…ウチは好いとーよ", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/18, /*event_id*/6042, /*comment_id*/301323, /*use_type*/2, /*index*/1, /*description*/"すーはー。落ち着いて…バッチリ決めてみせるけんね !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/19, /*event_id*/6042, /*comment_id*/301323, /*use_type*/3, /*index*/1, /*description*/"ウチがついとるけん、ドーンと胸張っていってきんしゃい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/20, /*event_id*/6042, /*comment_id*/301323, /*use_type*/4, /*index*/1, /*description*/"みんなで笑お ! 楽しい思い出、いっぱいつくるけん♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/21, /*event_id*/6042, /*comment_id*/301323, /*use_type*/5, /*index*/1, /*description*/"何がきたって、ウチは諦めんけんね。なんでもきんしゃい♪", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/22, /*event_id*/6042, /*comment_id*/301323, /*use_type*/100, /*index*/1, /*description*/"ようここまできたと ! 笑ってゴールを迎えるばい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/23, /*event_id*/6042, /*comment_id*/301323, /*use_type*/101, /*index*/1, /*description*/"何事も挑戦ばいね ! 新しい世界、きっと開けるけん !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/24, /*event_id*/6042, /*comment_id*/301323, /*use_type*/102, /*index*/1, /*description*/"あいらしかウチでも、みんなを笑顔にできると。\nウチ、思いきって挑戦してみてよかったと思っとるけん。\nもっともっといろんなことに挑戦しちゃるばい !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/25, /*event_id*/6042, /*comment_id*/301323, /*use_type*/102, /*index*/2, /*description*/"おお、みんなの注目がどんどん集まっとるけん…\n恥ずかしがってる場合じゃなかとね。\n一緒に、このチャンス掴みとるばいっ !", /*insert_word_type*/0);
INSERT INTO `rail_card_comment` VALUES (/*id*/26, /*event_id*/6042, /*comment_id*/301323, /*use_type*/102, /*index*/3, /*description*/"へへ…だんだん自信が湧いてきたと。\nこの調子で、どんどん前に進んでいくばい !\nウチの背に、ピッタリついてきんしゃーい♪", /*insert_word_type*/0);
