INSERT INTO `room_item_detail` VALUES (/*room_item_id*/16, /*level*/1, /*item_detail*/"落ち着いた雰囲気の漂う事務所の壁。ポスターなど様々な\n掲示物を張っても見栄えするベーシックなデザイン。\nアイドルのスケジュールなどが貼られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/17, /*level*/1, /*item_detail*/"落ち着いた雰囲気の、事務所の床用タイル。モノトーンの\nチェック柄で、どんな家具にも調和するデザイン。幼い\nアイドルが、よく黒いマスだけを歩く遊びをしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/18, /*level*/1, /*item_detail*/"世界的な有名建築家がデザインしたモダンなソファー。\n本革のイタリア製で大変高価。主に応接用として\n利用されている。この上で跳ねるとかなり怒られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/19, /*level*/1, /*item_detail*/"世界的な有名建築家がデザインしたモダンな2人掛け用の\nソファー。横幅が広く小柄な女性なら横になることが\nできる。緊急時(？)にはベッドになることもしばしば…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/20, /*level*/1, /*item_detail*/"マホガニー製のオフィス用高級テーブル。格調高い\n落ち着いたデザインで、このテーブルで宿題をすると、\n勉強がはかどるとの噂が…。テスト前は取り合いになる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/21, /*level*/1, /*item_detail*/"事務所の安らぎを演出する観葉植物。ちひろが毎日水を\nやって大切に育てており、アイドルたちがぶつかって\n倒すたびに、にこやかに微笑みながら怒っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
