INSERT INTO `room_item_detail` VALUES (/*room_item_id*/205, /*level*/1, /*item_detail*/"new generationsの好物が並べられたテーブル。\nフィナーレのあとは打ち上げパーティーを開こう。\nアイドル達もクラッカーを鳴らして盛り上がる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/210, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した\nクッション。ナナがウサミン星から乗ってきたという\nロケットをイメージしている。食べられません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000803);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/211, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n銅製のトロフィー。かなり高評価のセッションを\n演出した人に贈られる。ポンコツでは入手できない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/212, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n白銀のトロフィー。名人級のセッションをやり遂げた人に\n贈られる。刻まれたシリアルはオンリーワン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/213, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n純金のトロフィー。伝説級の超セッションを\n成し遂げた人だけに贈られる珠玉の逸品。超有機体製。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/220, /*level*/1, /*item_detail*/"Ｐが仮眠するためのカラフルなベッド。\n枕がたくさんあるので枕投げが始まることもしばしば。\n遊んだらちゃんと元に戻しましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/221, /*level*/1, /*item_detail*/"Ｐが仮眠するためのホワイトのベッド。\n深夜の収録や早朝のロケなどのために置かれている…\nはずだが、アイドルたちが遊ぶ場所になっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/222, /*level*/1, /*item_detail*/"Ｐが仮眠するためのブラックのベッド。\n誰かが眠っているときは静かにするのが暗黙のルール。\n時々ドッキリを仕掛けられるので安心はできない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/223, /*level*/1, /*item_detail*/"キャラクターの形をしたベッド。\n飛び込んだときの弾力は抜群だが、何だか悪いことをした\n気分になるので、皆、そっと乗ることにしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/224, /*level*/1, /*item_detail*/"ピンクの花が植えられている鉢植え。\n心優しいアイドル達が水やりをしているが、優しさが\n溢れてしまうことも。根腐れしないよう注意が必要。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/225, /*level*/1, /*item_detail*/"ブルーの花が植えられている鉢植え。\n花に詳しいアイドルが世話をするのが目撃されている。\n大きな花を咲かせたのはアイドル達の優しさの証明。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/226, /*level*/1, /*item_detail*/"オレンジの花が植えられている鉢植え。\n花好きなアイドルがこの花についてウキウキしながら\n語っていることも。育て方にはコツがいるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/227, /*level*/1, /*item_detail*/"緑が生い茂るプランター。\n目に疲れを覚えたらこのプランターの前で癒やされよう。\nきっとリフレッシュできるはず。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/228, /*level*/1, /*item_detail*/"カラフルな花が咲いているプランター。\n花の種類は相性や彩りを考えて、アイドルによって\n選ばれたもの。世話は気づいた人がしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/229, /*level*/1, /*item_detail*/"ダークブラウンのくまのぬいぐるみ。\nみんなが好き勝手呼ぶので、くま、テディ、大五郎など\n名前がたくさんあるが、文句ひとつ言わず座っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/230, /*level*/1, /*item_detail*/"ライトブラウンのくまのぬいぐるみ。\nアイドル達の会話にも黙って耳を傾ける善良なくま。\nアイドル達が人知れず流した涙も知っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/231, /*level*/1, /*item_detail*/"ダンボールの箱。かなり重い。\n何が入っているか誰も知らず、怖くて開けられないまま\n放置されている。噂によると、ちひろの……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/232, /*level*/1, /*item_detail*/"積み上げられたダンボールの箱。上の箱は開けたが\n下の箱は手つかず。あるアイドルが悪い予感がすると\n言ったので、そのままになってしまっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/233, /*level*/1, /*item_detail*/"コンクリートの壁。投球練習もできてしまうが、\n実際にやるとちひろやトレーナーに叱られるため、\n誰もやらなくなった。よく見ると的を消した跡が……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/234, /*level*/1, /*item_detail*/"コンクリートの床。かなり頑丈で、飛び跳ねても\n下の階に音が響かない。しかし、あまり暴れすぎると\nちひろが静かに迫ってくるので、加減は大事。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/240, /*level*/1, /*item_detail*/"ハロウィン風のクラシックなソファー。\nくもの巣はプリントなので安心して座れる。\n魔女っ娘ボウシで変装ごっこもできちゃうかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/241, /*level*/1, /*item_detail*/"ハロウィン風のパンプキンチェアー。\nカボチャの顔が怖くて座れないアイドルもいるらしい。\nよくみると愛嬌のある顔をしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/242, /*level*/1, /*item_detail*/"パープルのハロウィンゴースト風チェアー。\n背もたれのおばけは、顔色は悪いがたれ目でカワイイと\nアイドル達から意外に人気があるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/243, /*level*/1, /*item_detail*/"ハロウィンをイメージしたティーセット。\nおばけもカボチャも楽しく食べちゃおう。\nティースタンドに何が並んでいても驚かないこと。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/244, /*level*/1, /*item_detail*/"Ｐが仮眠するためのベッド。ハロウィン風。\nカボチャの王様が笑いながら寝ているように見えて\n不気味。アイドル達はクッションをそっとどかして使う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/245, /*level*/1, /*item_detail*/"ハロウィン風のナイトキャンドル。\nコウモリが飛んでいるように見えるため、\n虫とりあみで捕まえようとしたアイドルもいるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/246, /*level*/1, /*item_detail*/"オトメの愛がつまったカボチャのバスケット。\nアイドルの手作りお菓子がたくさん入っている。\nフタを開けた瞬間からお菓子の争奪戦が始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/247, /*level*/1, /*item_detail*/"ハロウィン風のライトスタンド。\nカボチャの形をしており、点いたりつかなかったりするが\n夜中にひとりでに光っていることも。気分屋らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501702);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/248, /*level*/1, /*item_detail*/"クモの巣が張った本棚。\n並んでいる本は洋書や魔導書だが、あくまで雰囲気を\n盛り上げるためのもの。掃除はまめにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/249, /*level*/1, /*item_detail*/"かぼちゃの置物。\nイスのように座れるが、一部のアイドルは放り投げる。\nどんな扱いを受けても不気味に笑い続けている…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/250, /*level*/1, /*item_detail*/"かぼちゃのフェンス。\nカボチャとコウモリがあしらわれていてホラー風味。\nいつでも見ている…。そんな声が聞こえてきそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/251, /*level*/1, /*item_detail*/"おばけのぬいぐるみ。ハロウィン風のアイテムなのに\n不気味さを感じさせない可愛らしさ。\nホラーが苦手なアイドル達も思わず抱きしめる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/252, /*level*/1, /*item_detail*/"棺桶。中で眠ることも出来るが寝心地は保証できない。\nキノコの栽培施設だと思えば怖くないので、勇気を出して\n開けてみよう。中には夢が詰まっている…かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/253, /*level*/1, /*item_detail*/"ハロウィンをイメージした壁紙。\nコウモリやカボチャがあしらわれている。アイドルが\n持ち込んだトカゲが張りついていることも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/254, /*level*/1, /*item_detail*/"ハロウィンをイメージした床。舞踏会のような\n雰囲気の絨毯。事務所に入るときにスカートの裾を\n持っておじぎをすれば、まるでお姫様気分♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/255, /*level*/1, /*item_detail*/"ハロウィンな雰囲気をかもしだす背景。\nビルはおばけのすみかになり、カボチャ達が笑いだす。\n遠く見える城では甘くて素敵な舞踏会が開かれる…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/260, /*level*/1, /*item_detail*/"凸レーションwith城ヶ崎美嘉のポスター。\nCDを購入した人だけが貰える限定の一枚。\n私色の笑顔を見つけた四人の収録帰りのオフショット。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/261, /*level*/1, /*item_detail*/"*(Asterisk)withなつななのポスター。\nCDを購入した人だけが貰える限定の一枚。\n夢色シグナルを追う四人の夕焼けの中のオフショット。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/262, /*level*/1, /*item_detail*/"LOVE LAIKA & Rosenburg Engel & *(Asterisk)の\nポスター。CDを購入した人だけが貰える限定の一枚。\n明日出会える笑顔を信じて、アイドル達は前へ向かう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/263, /*level*/1, /*item_detail*/"new generationsのポスター。\nCDを購入した人だけが貰える限定の一枚。\nたくさんの魔法とキセキ、そして笑顔がつまっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/270, /*level*/1, /*item_detail*/"イベント「Nation Blue」で獲得した壺。\n中には楓特製の梅干しが入っている。\n多くのアイドルがその酸っぱさの犠牲となった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/271, /*level*/1, /*item_detail*/"イベント「Nation Blue」で獲得した銅のトロフィー。\nイベントで活躍したＰに贈られる。\n諦めることなく自分を信じた証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/272, /*level*/1, /*item_detail*/"イベント「Nation Blue」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nトロフィーとその想いは輝き続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/273, /*level*/1, /*item_detail*/"イベント「Nation Blue」で獲得した純金のトロフィー。\n伝説級のステージを成し遂げたアイドルのＰだけに\n贈られる黄金の逸品。その輝きは僕ら照らしてる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/280, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した譜面台。\n水本ゆかりが自宅で愛用しているものと同一型で、\n美しさに定評がある。使えば誰でも指揮者気分。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/281, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nクラシカルなメロディーを紡いだ証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/282, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n聴衆のファンたちをハーモニーで魅了した証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/283, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いた証。\nその活躍にファンみんながスタンディングオベーション。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/290, /*level*/1, /*item_detail*/"江戸切子のグラス。\n赤・青・黄色と唯一無二の色で光輝いている。\n職人手作りの逸品のため、優しく取り扱おう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000306);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/291, /*level*/1, /*item_detail*/"マシュマロアスリートを目指せるマシン。\n飛び出たマシュマロを口でキャッチしなければならない。\n日本マシュマロキャッチ機構認定。マシュマロは食べ物。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/300, /*level*/1, /*item_detail*/"スタンダードなオフィスデスク。\nサインを書いたり、学生のアイドルは宿題をしたりする。\nそっと机上の整理整頓をするアイドルもいるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/301, /*level*/1, /*item_detail*/"温水、冷水両用のウォーターサーバー。\n美味しいミネラルウォーターを飲むことができる。\n事務所でのひとときにうるおいを与える一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/302, /*level*/1, /*item_detail*/"イタリア製のコーヒーメーカー。\n香り立つコーヒーで大人のブレイクタイムを楽しめる。\nミルクや砂糖をいれて楽しむもよし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/303, /*level*/1, /*item_detail*/"Ｐや担当アイドルの予定が書き込まれている\nスケジュールボード。Ｐの動向を知る情報源として\n活用されているという噂も…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/304, /*level*/1, /*item_detail*/"機能的なシステムキッチン。\nアイドルたちが料理番組に出る際には、\nここで練習することも。いつもピカピカ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/305, /*level*/1, /*item_detail*/"壁に取り付けられた棚。\n事務所の備品などを入れるためのもの。\nたまに、京都の和菓子が入っていることがあるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/306, /*level*/1, /*item_detail*/"モダンなデザインのS字型ラック。\n本や植物を飾って、事務所をオシャレ空間にしよう。\n意外と安定感はあるが、登ったりしてはいけない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/307, /*level*/1, /*item_detail*/"すこし大きめなサイズの観葉植物。\n緑がモッサリと茂っていて、隠れるには最適。\n一列に並べると生垣のようにみえる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/308, /*level*/1, /*item_detail*/"金魚を飼っている水槽。\n金魚はあるアイドルが縁日でとってきたものらしい。\nえさをやるのは幼いアイドルたちのお仕事らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/309, /*level*/1, /*item_detail*/"大型テレビ。アイドルたちが休憩中に見たり、\n出演番組をチェックするためのもの。\n録画予約がアニメでいっぱいなのは、誰の仕業？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/310, /*level*/1, /*item_detail*/"しろくろのねこのぬいぐるみ。\n溺愛するアイドルもいれば、撫でるだけのアイドルも。\nねこを愛する姿は、アイドルを映す鏡…かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/311, /*level*/1, /*item_detail*/"はいいろのねこのぬいぐるみ。\n多くのアイドルたちが適当な名前で呼んでいる。\nロシアンブルーっぽい色だが、汚れているわけではない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/312, /*level*/1, /*item_detail*/"その名のとおり窓のある壁。\n事務所のルームにもなんとなく解放感が生まれ、\nアイドルたちのやる気もなんとなくアップするかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/313, /*level*/1, /*item_detail*/"全面ガラス張りの壁。\n下を覗き込むとけっこう怖いので、高所恐怖症の大敵。\n解放感のあるルームになって、お仕事もはかどるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/320, /*level*/1, /*item_detail*/"シンデレラプロジェクト担当Ｐのデスク。\nアニメＢＤ/ＤＶＤ第６巻を購入した人が貰える限定品。\nＣＰ担当Ｐが一時的にルームの一角を間借りする。", /*effect_value_1*/70, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2006);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/330, /*level*/1, /*item_detail*/"イベント「Orange Sapphire」で獲得した、\nフォトボード。四季折々の思い出の写真が飾ってある。\n次はいっしょにどこにいこうか？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/331, /*level*/1, /*item_detail*/"イベント「Orange Sapphire」で獲得した、\n銅製のトロフィー。イベントで活躍したＰに\n贈られる。ラブリィなハート型。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/332, /*level*/1, /*item_detail*/"イベント「Orange Sapphire」で獲得した、\n白銀のトロフィー。アイドルを輝かせたＰに\n贈られる。キラキラきらめている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/333, /*level*/1, /*item_detail*/"イベント「Orange Sapphire」で獲得した、\n純金のトロフィー。伝説級のステージに導いたＰに\n贈られる。ずっと見つめていたくなる輝き。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/340, /*level*/1, /*item_detail*/"蒼いクリスタルがあしらわれたスタンドミラー。\n横に置いてあるリップは、奏がお気に入りの一品。\n使えばみずみずしい誘惑の唇になれるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/350, /*level*/1, /*item_detail*/"クリスマス仕様のパーティーテーブル。\n並んでいるごちそうはアイドルたちが持ち寄ったもの。\n料理の腕は各々だが、全てに気持ちは込められている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/351, /*level*/1, /*item_detail*/"クリスマス仕様のカバーがかかったソファー。\nカバーやトナカイのぬいぐるみはアイドルの私物。\nトナカイの名前はブリッツェンというらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/352, /*level*/1, /*item_detail*/"クリスマス仕様のベッド。\n冬の夜でもあたたかく眠れる。\n聖夜には枕元の靴下にプレゼントが……？", /*effect_value_1*/1, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/353, /*level*/1, /*item_detail*/"クリスマスツリー。前日にアイドル達が飾り付けたもの。\n美的感覚の違いにより苦難の連続だったが、\n結果的に最高の仕上がりとなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000303);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/354, /*level*/1, /*item_detail*/"ハンドベル。鳴らし方に個性が出る。\nエレガントに鳴らすアイドルもいれば、\n振り回すように鳴らすアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/355, /*level*/1, /*item_detail*/"重厚な造りの古めかしい薪暖炉。\nやかんには水を入れておく決まりだが、幼いアイドルは\n大人といっしょに扱うことになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/356, /*level*/1, /*item_detail*/"赤いポインセチアの植木。\nクリスマスカラーでおなじみだが、実は南国の植物。\n暖房器具の前に置こうとしたアイドルもいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/357, /*level*/1, /*item_detail*/"中身が楽しみなプレゼントの箱。\n中身がきちんと入っているかは分からない。\n一人ひとつなので、吟味して選ぼう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/358, /*level*/1, /*item_detail*/"イヴ・サンタクロースが連れているトナカイ、\nブリッツェンのぬいぐるみ。鼻水が出ているが\n布製なので抱きしめても大丈夫。親切設計。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/359, /*level*/1, /*item_detail*/"ゆきだるまのぬいぐるみ。\n心優しいアイドルが溶けてしまうといけないと\n寒空の下に出したこともあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/360, /*level*/1, /*item_detail*/"クリスマスリース。\n皆の活躍を願ってアイドル達が作ったもの。\n松ぼっくりは散歩好きなアイドルが拾ってきたもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/361, /*level*/1, /*item_detail*/"クリスマスガーランド。\nキラキラ光るので、幼いアイドルたちが触りたがる。\n繋げて飾って、部屋をクリスマス仕様にしてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/362, /*level*/1, /*item_detail*/"重厚な赤の壁紙。クラシックな雰囲気を演出する。\nいつもははしゃぎ回るアイドル達も\n少し大人っぽく…なったりならなかったり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/363, /*level*/1, /*item_detail*/"刺繍が施された緑の絨毯。\n北欧の家のようで、柔らかな暖かみが伝わってくる。\n温かい飲み物を手に、語り合いたくなる雰囲気だと好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/364, /*level*/1, /*item_detail*/"クリスマスの雰囲気を盛り上げる背景。\n雪が降る町には星とイルミネーションが輝いている。\n家々には温かな明かりが灯り、幸せな聖夜が訪れる…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/370, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した運命の扉。\n開くのは己の手。苦難が降りかかることもあるかも。\nそれを超えた先……その景色を見出すのもまた己だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/371, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅のトロフィー。\nイベントで活躍したＰに贈られる。\n自らを信じ、セカイに触れた証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/372, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルのセカイを閉ざす扉を解き放ったＰに贈られる。\nその可能性は他者を巻き込み、大きくなっていく…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/373, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\n伝説級のステージを成し遂げたアイドルのＰ…すなわち、\n真にセカイを理解した者だけが獲得できる特別な鍵。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/380, /*level*/1, /*item_detail*/"ドーナツのようなデザインの浮き輪。\n事務所ではクッションのように使う。これを置いてから、\n事務所でドーナツを食べるアイドルが増えたらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/390, /*level*/1, /*item_detail*/"おせちやお正月料理がたくさんのったテーブル。\n一年の活躍を祈願して、事務所のみんなで囲もう。\n普段は忙しいアイドル達も、この日ばかりは大集合。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/391, /*level*/1, /*item_detail*/"赤いざぶとん。分厚くて座り心地抜群。\nアイドルの実家から送られてきたものだが、\nせっかくだからと、事務所に置かれることになった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/392, /*level*/1, /*item_detail*/"紫のざぶとん。高級な布地でできている。\nお正月にみんなで集まるときに必要だからと、\nあるアイドルが持ち込んだものらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/393, /*level*/1, /*item_detail*/"暖かみのあふれるこたつ。\n仁奈のお気に入りのカメのぬいぐるみが常駐している。\n一回入るとなかなか出られないので注意。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/394, /*level*/1, /*item_detail*/"ぴにゃこら太の福笑い。\nアイドル達が挑戦しては結果を見せてくる。\n事務所内福笑い王座決定戦を開催するとかしないとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/395, /*level*/1, /*item_detail*/"華やかな門松。\n中心にはアイドルのともだちであるウッキッキーが\n据えられ、可愛らしいと好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/396, /*level*/1, /*item_detail*/"縁起のよいしめかざり。\n神様を迎える清浄な場所であることを示しており、\nアイドル達が自主的に掃除をしてから飾った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/397, /*level*/1, /*item_detail*/"ストーブ。触れると危ないので柵で覆われている。\n朝一で事務所に来て、このストーブに火を入れて\nルームを暖めておくのが、ちひろさんの冬の習慣。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/398, /*level*/1, /*item_detail*/"松の盆栽。\n植物好きのアイドルがいじっていて、丁寧な手入れが\nなされている。勝手に触るのはやめておこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/399, /*level*/1, /*item_detail*/"ししまいのぬいぐるみ。\n中には入れないが、手を入れて口をパクパクさせることが\nできる。アイドルによって動かし方も様々に遊べる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/400, /*level*/1, /*item_detail*/"だるま。目を入れるのは願いごとが叶ってから。\nまだ夢の途中だが、いつかみんなで夢を叶えて\n目を入れることを約束した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/401, /*level*/1, /*item_detail*/"床の間。事務所を和の雰囲気に変えられる。\n飾られている掛け軸は高価なものらしいが、\n達筆すぎて何が書いてあるのか誰も分からない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/402, /*level*/1, /*item_detail*/"木の衝立。上質な木で造られている。\n重量があり、足の小指をぶつけたアイドルが\n痛みにうめいていたことも。ついたてと読みます。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/403, /*level*/1, /*item_detail*/"和風の壁紙。\n部屋を和の雰囲気で包み込む。幼いアイドル達は\n障子を前にしてウズウズした様子を見せているが…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/404, /*level*/1, /*item_detail*/"新品の畳。\n和の雰囲気が落ち着くというアイドルも多い。\n事務所なので土足でいいことになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/405, /*level*/1, /*item_detail*/"お正月をテーマにした背景。\n富士山近くの空を、鷹がどこからか持ってきた茄子を\n持って飛んでいる。縁起のいい一年を過ごせるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/410, /*level*/1, /*item_detail*/"イベント「Snow Wings」で獲得したキャンディツリー。\n唯がオススメのキャンディを補充してくれるので、\nみんなで楽しく味わってはしゃいじゃおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/411, /*level*/1, /*item_detail*/"イベント「Snow Wings」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドル達は\nファンの心に飛び込むステージを届けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/412, /*level*/1, /*item_detail*/"イベント「Snow Wings」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nその輝きは光の海にも負けない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/413, /*level*/1, /*item_detail*/"イベント「Snow Wings」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドル達に羽を授け、夢へと羽ばたかせた証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/420, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したミニステージ。\nアイドルがステージに立てば、\nゴキゲンなParty Nightが始まる。思いきり楽しもう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/421, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\n手と手と手をつなぎ、Dance♪しよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/422, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n初めて会った人も大事な仲間になれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/423, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに\n導いたＰに贈られる。パーティーは終わらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/430, /*level*/1, /*item_detail*/"渋谷凛のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n凛のクールな眼差しが見る者をまっすぐに見つめる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/431, /*level*/1, /*item_detail*/"島村卯月のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n卯月のキュートなスマイルが、昨日より光る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/432, /*level*/1, /*item_detail*/"本田未央のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\nパッション弾ける未央と明日へ飛びだそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/433, /*level*/1, /*item_detail*/"諸星きらりと双葉杏のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n頬を寄せ合う姿からも、二人の信頼関係がうかがえる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/434, /*level*/1, /*item_detail*/"アナスタシアと神崎蘭子と新田美波のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n新しい世界を見つめる三人の瞳は輝いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/435, /*level*/1, /*item_detail*/"城ヶ崎美嘉と城ヶ崎莉嘉と赤城みりあのポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n抑えきれないワクワクが笑顔となって弾けている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/436, /*level*/1, /*item_detail*/"三村かな子と緒方智絵里のポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n支え合う二人の幸せそうな表情が見た者を笑顔にする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/437, /*level*/1, /*item_detail*/"多田李衣菜と前川みくのポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n背中合わせだが、バランスのとれた二人の笑顔がきらめく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/438, /*level*/1, /*item_detail*/"シンデレラプロジェクトのメンバーのポスター。\nBlu-ray/DVDを購入した人だけが貰える限定の一枚。\n彼女たちの魔法が解けることは、きっとないだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/440, /*level*/1, /*item_detail*/"スノードーム。聖夜に枕元の靴下に入っていた。\n差出人は不明だが、雪が降り続けるさまは\n優しい雰囲気だとアイドル達の間で好評の贈り物。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000304);
