INSERT INTO `room_item_detail` VALUES (/*room_item_id*/460, /*level*/1, /*item_detail*/"ピンク色の鍵盤ハーモニカ。\n事務所でも練習ができるように千枝が持ち込んだ。\nその音色は、みんなをどこか懐かしい気持ちにさせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/470, /*level*/1, /*item_detail*/"イベント「パステルピンクな恋」で獲得した藤色の和傘。\n紗枝のお気に入りで撮影に使ったりもする。\nさせばイイコトが起こりそうで心がふわっと踊る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504401);
