INSERT INTO `room_item_detail` VALUES (/*room_item_id*/133, /*level*/1, /*item_detail*/"ピンクのブタさん貯金箱。単なる貯金箱だと思ったら\n大間違い。金運上昇のご利益あらたかなスピリチュアル・\nアイテム…らしい。可愛いので、アイドルにハグされる。", /*effect_value_1*/10, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/134, /*level*/1, /*item_detail*/"シルバーに輝くブタさん貯金箱。金運上昇のラッキー\nアイテム。この中にお金を貯めると、ちょっと増える…\nなんて噂も。硬派なアイドルにも、渋い ! と評判。", /*effect_value_1*/30, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
