INSERT INTO `room_item_detail` VALUES (/*room_item_id*/200096, /*level*/1, /*item_detail*/"新田美波のポスター。\n気高く凜々しい美しさとはかなさを切り取った、\n女神のような一枚。美波自身は見ると照れるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
