INSERT INTO `room_item_detail` VALUES (/*room_item_id*/491, /*level*/1, /*item_detail*/"食品の鮮度を守るため、設置された冷蔵庫。\n物を入れるときは、自分の名前を書くきまり。時々、\nアイドルたちからＰへの差し入れが入っていることも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504701);
