INSERT INTO `room_item_detail` VALUES (/*room_item_id*/495, /*level*/1, /*item_detail*/"フワフワしていてラブリーなハートのラグマット。\nこのラグの上でラブソングの練習をすると、うまく\nなるという噂が、まことしやかに囁かれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/496, /*level*/1, /*item_detail*/"板チョコっぽいデザインのパーティション。\n幼いアイドルが溶けてしまうと心配していたが、\n木製だと知ると、ほっとした表情を見せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/497, /*level*/1, /*item_detail*/"チョコレートモチーフのバレンタイン風ガーランド。\nいろんな味のチョコレートが並んでいるように見える。\n皆は何味のチョコレートが好きか論争を繰り広げている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/498, /*level*/1, /*item_detail*/"チョコ好きにはたまらないチョコレートファウンテン。\n無限にチョコレートが出てくる魔法のアイテム…ではなく\nチョコレートが循環している。皆が仕組みを知りたがる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/499, /*level*/1, /*item_detail*/"ラブリーな雰囲気の壁紙。ハートがあしらわれ、\n女の子らしい色使い。アイドルたちの間で、\nＰの女子力の表れではないかと話題になっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/500, /*level*/1, /*item_detail*/"ピンク色の絨毯。ハートが集まり、大きなハートを\nつくっている。中心に立っていると、たくさんの人の\n絆を感じられて、なんだか心が温かい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/501, /*level*/1, /*item_detail*/"バレンタインがテーマの背景。\nビルごとチョコレートのラッピングをしてしまおう。\n甘いあまい気持ちに包まれるように……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/510, /*level*/1, /*item_detail*/"アイドルたちがチョコを作るキッチン。\n協力しながら、ときに言い合いをしながら作っている。\nチョコの行方はバレンタイン当日にわかるらしい。", /*effect_value_1*/1, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/520, /*level*/1, /*item_detail*/"イベント「オルゴールの小箱」で獲得したオルゴール\nボックス。アイドルたちが歌った曲のオルゴールver.を\n聴くことができる。その音色にみながウットリ……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/521, /*level*/1, /*item_detail*/"イベント「オルゴールの小箱」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアナスタシアの持っている望遠鏡にそっくり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/522, /*level*/1, /*item_detail*/"イベント「オルゴールの小箱」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\n望遠鏡を覗けば、未来の輪郭も見えるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/523, /*level*/1, /*item_detail*/"イベント「オルゴールの小箱」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに贈られる。望遠鏡を覗くとファンの笑顔が見えたという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/530, /*level*/1, /*item_detail*/"美城専務がやってくる際に使用するドア。\nBlu-ray/DVDを購入した人だけが貰える限定品。\n時々、美城専務が訪ねてきてお言葉をもらえる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2008);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/540, /*level*/1, /*item_detail*/"村上巴が愛用している将棋盤。重厚なつくり。\n巴が事務所のみんなにルールを教えてあげた。\n幼いアイドルはくずし将棋で遊ぶこともある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/550, /*level*/1, /*item_detail*/"ドリフトスピリッツとの期間限定コラボアイテム。\n島村卯月SP。卯月の姿があしらわれている。\nキュートなピンクで、ルームをポップに演出する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/551, /*level*/1, /*item_detail*/"ドリフトスピリッツとの期間限定コラボアイテム。\n渋谷凛SP。凛の姿があしらわれている。\nブルーのボディでスタイリッシュに走り抜けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/552, /*level*/1, /*item_detail*/"ドリフトスピリッツとの期間限定コラボアイテム。\n本田未央SP。未央の姿があしらわれている。金運が\n高まりそうなイエローのボディはインテリアとしても◎。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/553, /*level*/1, /*item_detail*/"ドリフトスピリッツとの期間限定コラボアイテム。\nデレステSP。ステージで輝くアイドルたちが\nあしらわれている。トップアイドルへの道を走り抜こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/560, /*level*/1, /*item_detail*/"いちごをモチーフにした可愛らしいソファー。\nありすが大層気に入っていて、自室にも同じものの購入を\n考えているという。ルームを鮮やかに彩る一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/561, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n立ちはだかる困難も恐れず、ステージをやりきった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/562, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\nスポットライトに感動の涙を隠し、歌い続けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/563, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは自分色の旗を掲げ、これからも進む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/570, /*level*/1, /*item_detail*/"ひな人形を飾ったひな壇。\nひなまつりといえばコレ ! アイドルたちの成長や\n幸せを願って、ルームにもひな人形を飾ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000305);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/571, /*level*/1, /*item_detail*/"ひな壇と一緒に飾るぼんぼり。その名の通り、ぼんやりと\nした光を放つ。和のインテリア照明としてもおすすめ。\nこれを見たアイドルたちはあの童謡を思い出すという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/572, /*level*/1, /*item_detail*/"ひなまつり用のお菓子を載せたお菓子タワー。カラフルで\n可愛いお菓子がたくさん。誰でも食べてよいことに\nなっているが、食事の前には注意が飛び交う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/573, /*level*/1, /*item_detail*/"豪華な金屏風。その美しさたるや言葉では言い表せない。\nこの屏風の前で、幼いアイドルたちがおひなさまごっこを\nすることも。年長者は微笑ましい気持ちでそれを見守る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/574, /*level*/1, /*item_detail*/"可愛らしさを備えた和テイストの壁紙。和紙を使用して\nおり、取扱いには注意が必要。しかし、そう言われると\nイタズラしたくなるアイドルもいるようで…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/575, /*level*/1, /*item_detail*/"和菓子のような色使いの絨毯。ルームを可愛らしくも\n落ち着いた雰囲気にするはずが…アイドルたちはマス目で\nゲームをしたり、ラインに沿って歩いたりと忙しい様子。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/576, /*level*/1, /*item_detail*/"ひなまつりをテーマにした背景。建物ごと和テイストに\n模様替え。桃の節句らしく、桃の花が咲き誇っている。\n春の訪れを感じられるとアイドルたちにも好評だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/580, /*level*/1, /*item_detail*/"シンプルで機能的な学校机。\n学校を卒業したアイドルにも懐かしいと人気の品。\n授業の受け方には性格が出てしまうものらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/581, /*level*/1, /*item_detail*/"先生が立つ教卓。\nこの教卓に立つと、ついつい先生の真似をしてしまう。\nアイドルたちの個性あふれる授業を覗いてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/582, /*level*/1, /*item_detail*/"ロッカー。アイドルたちがかくれんぼの隠れ場所として\n使うことも。Ｐを驚かせたり、見守ったりするために\n入るアイドルもいるらしい。開ける際は注意が必要。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/583, /*level*/1, /*item_detail*/"重要な資料が並んでいる資料棚。\n並んでいる資料の多くは、ちひろが整理したもの。\n宣材写真やプロダクションの関係資料が入っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/584, /*level*/1, /*item_detail*/"さまざまな本が並ぶ本棚。並んでいる本の多くは\nアイドルたちが持ち込んだもの。年長者が年少者に\n読み聞かせをしたりと、交流の場にもなっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/585, /*level*/1, /*item_detail*/"黒板。\n使った後はきちんと消しておくという決まり。\n消し忘れた人にはちひろの手伝いが課せられる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/586, /*level*/1, /*item_detail*/"学校にあるような銀の壁掛け時計。\n早くお昼やおやつの時間がこないかと、\nアイドルたちがよく見上げている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/587, /*level*/1, /*item_detail*/"学校の雰囲気をかもしだす壁紙。\nこの壁紙が貼られたルームでは真面目に過ごそうという\n気になるらしい。成績が上がったアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/588, /*level*/1, /*item_detail*/"教室の床。丈夫で掃除がしやすいので、\n掃除やワックスがけはみんなでやるようになった。\n友情は何気ないことでも大きく育つのだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/589, /*level*/1, /*item_detail*/"桜をテーマにした背景。\n並木道に桜が咲き誇っている。日が落ちて、\nライトアップされた夜桜も幻想的で美しいと人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/590, /*level*/1, /*item_detail*/"イベント「絶対特権主張しますっ ! 」で獲得した\nケーキ型のクッション。愛梨が作ったケーキを\nモチーフに作られたもの。なぜか甘い匂いがする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/591, /*level*/1, /*item_detail*/"イベント「絶対特権主張しますっ ! 」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは勇気を持ってステージに挑戦 ! した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/592, /*level*/1, /*item_detail*/"イベント「絶対特権主張しますっ ! 」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちはファンの視線を独占 ! した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/593, /*level*/1, /*item_detail*/"イベント「絶対特権主張しますっ ! 」で獲得した純金\nのトロフィー。アイドルを伝説級のステージに導いた\nＰに贈られる。アイドルたちは絶対特権を主張したっ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/600, /*level*/1, /*item_detail*/"「とある事務員の一日」を達成した報酬。うさぎの\nぬいぐるみが置かれているソファー。このぬいぐるみを\n抱いていたら、なんだか眠たくなってきて……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/610, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した太鼓。LIVEで薫が\n使ったもの。少し傷んでいるのは一生懸命練習した証。\nリズムに合わせ、みんなで叩いて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/611, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\nいっせーのハイ ! ! でステージへ飛び出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/612, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。約束した\nステージで、止まらないEmotionが輝いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/613, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアンコールでは、会場のみんなで手をつないで歌った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/620, /*level*/1, /*item_detail*/"古風なちゃぶ台。端午の節句の定番のお菓子である\n柏餅とちまきが載っている。柏餅とちまきは、ちひろが\n出勤時に事務所の近所の和菓子屋で買ってきたもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/621, /*level*/1, /*item_detail*/"木のテーブル。新聞紙が置かれていて、\n兜などいろんなものを作って遊べる。\nそのわんぱくさは男子顔負け。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/622, /*level*/1, /*item_detail*/"兜飾り。「身を守る」という意味から、\n幸福を祈願して飾られる。アイドル界を戦う\n彼女たちにも幸多くあらんことを……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/623, /*level*/1, /*item_detail*/"白い柏餅をかたどったクッション。\n美味しそうと飛びつくアイドルもいるが、もちろん\n食べられない。もちもちの触感が食欲をそそるが……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/624, /*level*/1, /*item_detail*/"ピンクの柏餅をかたどったクッション。\nふわふわしていて、ボリューム感がある。\n飛び込むと、もっちり包み込んでくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/625, /*level*/1, /*item_detail*/"こいのぼりの壁飾り。可愛らしいこいが並んでいる。\n魚好きのアイドルがこいのぼりの由来を話して、\n幼いアイドルから感心されていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/626, /*level*/1, /*item_detail*/"菖蒲の花飾り。勝負や尚武に通じている。\nアイドルとして勝負できるように、という\n思いを込め、アイドルたちが自主的に飾った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/627, /*level*/1, /*item_detail*/"ブラウンの木枠の壁掛け観葉植物。\nルームを癒しの緑で飾る一品。カフェのような\n雰囲気になったと、アイドルからの評判は上々。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/628, /*level*/1, /*item_detail*/"ホワイトの木枠の壁掛け観葉植物。\n清潔な雰囲気に包まれる一品。アイドルたちが\n摘んできた花が加わっていることもある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/629, /*level*/1, /*item_detail*/"レールライト。ライトの位置を変えられて便利。\nだが、高い位置にあるため、多くのアイドルたちは\n手が届かないので、背の高いアイドルが手助けする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/630, /*level*/1, /*item_detail*/"ガラスのパーティション。向こう側が透けてしまうので、\n意味がないかと思いきや、個性的な使い方をしている\nアイドルも。パーティション越しに手を重ねたり……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/631, /*level*/1, /*item_detail*/"あおぞらをモチーフにした壁紙。\nスカイブルーでルームをさわやかな雰囲気に。\n飛んでいるような気分が味わえるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/632, /*level*/1, /*item_detail*/"シンプルなグリーンの床。\n目に優しく、癒し効果も期待できるかも。\n草原に来たようだと寝ころぶアイドルが増えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/633, /*level*/1, /*item_detail*/"ストライプ模様のグリーン床。\n幼いアイドルたちが、ストライプ模様の上をまっすぐ\n歩けるか競っているのをよく見かける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/634, /*level*/1, /*item_detail*/"市松模様のグリーンの床。\n将棋好きのアイドルが真っ先に食いついたという。\nしかし、マス目が足りず、人間将棋は諦めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/635, /*level*/1, /*item_detail*/"クマトモに出てくるクマのぬいぐるみ。\nおしゃべりはしないが、親友になることはできる。\nいつかトクベツになるその日を信じてお話ししよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/636, /*level*/1, /*item_detail*/"にゅーすぼっとくんのぬいぐるみ。ひたむきで、\nつつましやかな生活を送るにゅーすぼっとくん。\nアイドルたちも共感できる様子。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/640, /*level*/1, /*item_detail*/"クールなミニステージ。NYの街並みをイメージした\nステージで、踊り明かそう ! ダンス好きなアイドルが\n集い、思いおもいのダンスを披露する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/650, /*level*/1, /*item_detail*/"イベント「つぼみ」で獲得したアヤしい実験セット。\n志希が愛用している実験器具と同じもの。実験を\n体験することができる。薬品の扱いには注意しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/651, /*level*/1, /*item_detail*/"イベント「つぼみ」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\n小さなつぼみは未来を求め、高なり出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/652, /*level*/1, /*item_detail*/"イベント「つぼみ」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルたちは小さな光をファンに届けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/653, /*level*/1, /*item_detail*/"イベント「つぼみ」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドル達は希望の種を芽吹かせ、大きな花を咲かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/660, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した衣装スタンド。\n少女たちが戦うために身にまとった白の制服。\n戦乙女は、己の意志で未来を掴むために歌う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/661, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\n少女たちは変わりたいという願いを叶えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/662, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n少女たちはステージに立ち続け、希望を示した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/663, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに\n導いたＰに贈られる。少女たちは願った未来を掴んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/670, /*level*/1, /*item_detail*/"ナチュラルなウッドデスク。\nルームをおしゃれに演出する。このデスクに座り、\n飲むコーヒーは美味しく感じるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/671, /*level*/1, /*item_detail*/"ウッドテーブル。\n憩いの場となっており、みなが集まる。\nアイドルたちがプライベートの話をすることも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/672, /*level*/1, /*item_detail*/"ホワイトのカバーがかかったウッドソファー。\n弾力があり、座り心地がよい。\n乗って跳ねるのは禁止されている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/673, /*level*/1, /*item_detail*/"オリーブ色のカバーがかかったウッドソファー。\nこのカバーは何色か？という話題で、アイドルたちが\n盛り上がった。結果、「オリーブ色」と呼ぶことに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/674, /*level*/1, /*item_detail*/"ホワイトのウッドスツール。\n森の中で切り株に座っている気分を味わえる。\n「森のくまさんごっこ」に使うアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/675, /*level*/1, /*item_detail*/"オリーブ色のウッドスツール。木のいい香りがして、\n座るだけで癒される。みなが座りたいので、\nイス取りゲームに発展することもしばしば。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/676, /*level*/1, /*item_detail*/"サークルベンチ。中央に木が生えているので、\nルームにいながら、自然に触れられる。\n木にカブトムシがいて、騒ぎになったこともある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000105);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/677, /*level*/1, /*item_detail*/"ハンモック。アイドルたちが落ちないように、\nＰが使い方を指導した。初めはうまく乗れなかった\nアイドルたちも、今ではここでぐっすり眠れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/678, /*level*/1, /*item_detail*/"ホワイトのサークルラグ。\nナチュラルなインテリアにぴったり。これを選んだ\nセンスにアイドルたちもウットリ…かも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/679, /*level*/1, /*item_detail*/"オリーブ色のサークルラグ。\n色合いが目に優しい。このラグの上に立つと、\nまるで草原に立っているような気分が味わえるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/680, /*level*/1, /*item_detail*/"木目がきれいなウッドチェスト。\n引き出しの中には、手作りのアクセサリーや\n押し花のしおりなど、可愛らしい小物がいっぱい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/681, /*level*/1, /*item_detail*/"コーヒーメーカーなどが置かれたウッドチェスト。\nコーヒーを飲んだことがないアイドルが、\nチャレンジするきっかけになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503002);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/682, /*level*/1, /*item_detail*/"ウッドラック。置くだけでオシャレなインテリアに。\nメモスタンドには日替わりでアイドルのサインが\n飾られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/683, /*level*/1, /*item_detail*/"コートハンガー。上着やカバンをかけておける。\nルームに上着を脱ぎっぱなしにしておかず、\nここにかけるようにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/684, /*level*/1, /*item_detail*/"ウッドアロマディフューザー。\nルームを潤わせ、いい香りにする。\n祈りの儀式に使われているという噂も……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/685, /*level*/1, /*item_detail*/"ウッドウォールミラー。アイドルたちが\n身だしなみを整えるのに使っている。\nこの鏡の前ではナチュラルな表情になれるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/686, /*level*/1, /*item_detail*/"ナチュラルなウッドウォールクロック。\nどんなインテリアにも調和するデザイン。\nゆったりとした時間の流れを感じられる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/687, /*level*/1, /*item_detail*/"ウッドパーティション。\n木戸のように見えるが、パーティションである。\nお芝居の練習をする際、大道具代わりに使われることも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/688, /*level*/1, /*item_detail*/"木目が美しいブラウンの壁紙。\nナチュラルテイストのインテリアにぴったり。\nアイドルたちが背くらべをした跡がある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/689, /*level*/1, /*item_detail*/"木目が美しいダークブラウンの壁紙。\n木の温かみを感じられ、落ち着いた気持ちになれる。\n休日にルームへ顔を出すアイドルが増えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/690, /*level*/1, /*item_detail*/"ブラウンの木目調の床。\n木のいい香りがして、気分をリフレッシュできる。\n走ると、滑るので気をつけ……言ってるそばから……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/691, /*level*/1, /*item_detail*/"ダークブラウンの木目調の床。\n木目が人の顔のように見えると、アイドルたちが\n騒然となった。家具を置いてごまかしてしまおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/700, /*level*/1, /*item_detail*/"梨沙をナメまくった馬……ではなく、馬のスツール。\n乗ることもできる。初めは逃げ腰だった梨沙も、\n乗っているうちに、愛着がわいたようだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000804);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/710, /*level*/1, /*item_detail*/"イベント「明日また会えるよね」で獲得した\n薔薇のブランコ。使われている薔薇にトゲはないので、\n安心して遊べる。レディの気分を味わおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/711, /*level*/1, /*item_detail*/"イベント「明日また会えるよね」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちのためにたくさんのファンが駆けつけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/712, /*level*/1, /*item_detail*/"イベント「明日また会えるよね」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは夢のようなステージの余韻に浸っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/713, /*level*/1, /*item_detail*/"イベント「明日また会えるよね」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。アイドルたちは宝石みたいな日々に感謝した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/720, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したパンチングマシン。\n気合上等な拳をお見舞いして、高得点を狙おう。\n拓海が出したハイスコアに並ぶ者は現れるか…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/721, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルは気合上等 ! 熱い絆で夢に特攻した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/722, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n走志走愛 ! 純情をかっ飛ばして、限界を追い越した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/723, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに\n導いたＰに贈られる。行こうぜ、ステージの向こうへ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/730, /*level*/1, /*item_detail*/"エレガントなティーセット。\nここでティーをたしなめば、気分はたちまちお嬢様。\nローズヒップティーが一番の人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/731, /*level*/1, /*item_detail*/"エレガントなチェア。\nあまり座らないアイドルもいる。\n可愛らしすぎて、恥ずかしいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/732, /*level*/1, /*item_detail*/"エレガントなベッド。\n高級素材を使ったベッドで、寝心地は最高。\nお姫様気分で王子様を待つアイドルも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/733, /*level*/1, /*item_detail*/"薔薇模様のラグ。\nふわふわの触り心地で、寝ころびたくなる。\n誰かが香水をかけたのか、いい香りがする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/734, /*level*/1, /*item_detail*/"エレガントなミニキャビネット。\n載っている緑の物体は、ぴにゃこら太の卵…\nではなく、ロココ調のジュエリーボックスである。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/735, /*level*/1, /*item_detail*/"エレガントなキャビネット。\nエレガントなミラー、ジュエリーボックス、香水などが\n載っており、何から何までエレガント。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/736, /*level*/1, /*item_detail*/"小さな噴水。\n薔薇の花びらがたゆたう様は、堕天使が運命に\n翻弄される様を彷彿とさせる…らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/737, /*level*/1, /*item_detail*/"薔薇の花壇。\nアイドルたちがこの薔薇を愛でながら、\n「この花のように美しく咲きたい」と話していた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/738, /*level*/1, /*item_detail*/"薔薇のアーチ。\nこの下をくぐると、ふんわりいい香りがする。\nアイドルたちの癒しスポットになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/739, /*level*/1, /*item_detail*/"ブーケスタンド。\n撮影で使われるブーケが飾られている。アイドルたちは\nこのブーケを持つ日を心待ちにしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/740, /*level*/1, /*item_detail*/"撮影で、蘭子が着用したウェディングドレス。\nこのドレスを着ることが決まった日から、\n時々衣装室を訪れる蘭子が目撃されている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/741, /*level*/1, /*item_detail*/"撮影で、みくが着用したウェディングドレス。\n着ていると心躍るが、猫チャンを抱っこできない\nことが難点だと、みくが話していた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/742, /*level*/1, /*item_detail*/"キャンドルスタンド。\nルームで火を使うには、ちひろの許可が必要。\n火を灯すと、ルームは幻想的な雰囲気に包まれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/743, /*level*/1, /*item_detail*/"エレガントなウォールミラー。\nお姫様気分で、世界で誰が一番キレイか問いかける\nアイドルも。見られると、ちょっと恥ずかしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/744, /*level*/1, /*item_detail*/"薔薇のリース。\n白と薄ピンクが、ルームを優しく彩る。\n顔をはめて遊んでいるアイドルがいたらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/745, /*level*/1, /*item_detail*/"傘立て。\n常に何本かの傘が入っている。\n傘を開くと、びっくりすることが起こるかも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/746, /*level*/1, /*item_detail*/"ステンドグラスのある壁。\n光が差し込み、ルームが荘厳な雰囲気に。\nチャペルのようだとうっとりするアイドルも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/747, /*level*/1, /*item_detail*/"薔薇模様が描かれた大理石の床。\n高級感と華やかさを兼ね備えているが、\nかなり滑りやすいので、注意が必要。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/750, /*level*/1, /*item_detail*/"美玲お気に入りの店のマスコットキャラクターの\nぬいぐるみ。美玲がルームに持ち込んだところ、\nカワイイと評判に。美玲もちょっと嬉しそうだった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/760, /*level*/1, /*item_detail*/"イベント「咲いてJewel」で獲得した文香愛用の読書机。\n文香の愛読書が並んでいる。仲間におすすめしようと、\n持ってきた本も含まれているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/761, /*level*/1, /*item_detail*/"イベント「咲いてJewel」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは飾らない心をさらけ出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/762, /*level*/1, /*item_detail*/"イベント「咲いてJewel」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは色づいた歌声を届け、未来を輝かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/763, /*level*/1, /*item_detail*/"イベント「咲いてJewel」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。アイドルたちは自分というJewelを咲かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/770, /*level*/1, /*item_detail*/"レトロな蓄音機。\nアイドルの曲をルームに流せる。収納可能。\nシックな雰囲気で、インテリアにもおすすめ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/771, /*level*/1, /*item_detail*/"ポップな赤色のラジカセ。\nアイドルの曲をルームに流せる。収納可能。\n「カセットってなに？」と聞く幼いアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/772, /*level*/1, /*item_detail*/"シャープなブラックのコンポ。\nアイドルの曲をルームに流せる。収納可能。\n音にこだわるアイドルたちに評判の一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/780, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\nまゆお気に入りのデザインの日記帳。\nルームで、アイドルたちが交換日記として使っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/781, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nファンはみな、「目が合った気がする」と言っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/782, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちの輝きに、ファンは目が離せなくなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/783, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。ステージを見たファンは運命の愛を知った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/790, /*level*/1, /*item_detail*/"くつろぎビーチチェア。\n夏のルームでゆったりくつろぐためのアイテム。\nお仕事で忙しいアイドルたちもリラックスモード。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/791, /*level*/1, /*item_detail*/"ナイスパラソル。\nその名のとおり、ナイスなパラソル。\nアイドルたちが日焼けしないようにさしてあげよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/792, /*level*/1, /*item_detail*/"くつろぎビーチチェアとナイスパラソルのセット。\nこれなら日焼けを気にすることなく、お昼寝できる。\nアツく、ルームで、パラダイス !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/793, /*level*/1, /*item_detail*/"みずみずしいすいか。\nアイドルたちがすいか割りをするために持ち込んだもの。\n上手に割って、美味しいすいかを食べよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/794, /*level*/1, /*item_detail*/"やしの木。これを置けば、そこはもはや南国…。\nお仕事を忘れてバカンス気分。\nはしゃぎすぎて怒られないようにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/795, /*level*/1, /*item_detail*/"砂山。\nアイドルたちが埋まったり、砂の城を造ったりする。\n夏の砂浜セットの中でもイチオシのアイテムです !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/796, /*level*/1, /*item_detail*/"クーラーボックス。\n中には冷たい飲み物が入っている。\nＰがアイドルたちの体調に気をつかって用意したもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/797, /*level*/1, /*item_detail*/"うさぎの浮き輪とビーチボール。\n杏の私物で、海水浴にも持っていった。\n安定性は抜群で、ぷかぷかぐだぐだするのに最適。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505702);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/798, /*level*/1, /*item_detail*/"バナナボート。\n南国で仕事があったときに、アイドルがお土産として\n買ってきたもの。倒れないように注意すること。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000805);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/799, /*level*/1, /*item_detail*/"ひまわりのプランター。\nひまわりのようにいつも明るいほうを向いていたいと、\nアイドルたちが和やかに話していた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/800, /*level*/1, /*item_detail*/"ハイビスカスの鉢植え。\n鮮やかな赤がルームに彩りを加える。たくさん水が\n必要な花なので、水やり当番を決めて育てることに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/801, /*level*/1, /*item_detail*/"サーフボードラック。\nLIVEのセットとして使われたが、実際に使える。\n今度、海に行ったとき、みんなで波に乗ってみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/802, /*level*/1, /*item_detail*/"やしの木と砂浜が描かれた壁。\nまるで南国にいるかのような解放感が味わえる。\nやしの木にハンモックを吊るそうとしたアイドルがいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/803, /*level*/1, /*item_detail*/"砂浜＆海をあしらったビーチな床。\n波打ち際で真夏のアバンチュールごっこもできるかも。\n室内だということを忘れてしまいそうだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/804, /*level*/1, /*item_detail*/"夏の海をテーマにした背景。\nロマンチックな南国の海をモチーフにしたもの。\nアイドルたちもその雰囲気にウットリ…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/810, /*level*/1, /*item_detail*/"陶芸スペース。みなにも陶芸に挑戦してほしいと、\n肇が持ち込んだもの。失敗を恐れず、\nまずは挑戦してみることが表現の第一歩。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/811, /*level*/1, /*item_detail*/"ねこっぴーの抱きまくら。\n大きな抱きまくらでフワフワ。寝心地は抜群。\nこの上で眠ると、夕方まで起きられないことも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/812, /*level*/1, /*item_detail*/"イベント「きみにいっぱい☆」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちのココロは弾けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/813, /*level*/1, /*item_detail*/"イベント「きみにいっぱい☆」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちのワクワクって笑顔が飛び出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/814, /*level*/1, /*item_detail*/"イベント「きみにいっぱい☆」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。めーいっぱい☆ハッピーにアイを歌った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/820, /*level*/1, /*item_detail*/"うえきちゃん。\n踊りません。動きません。植木だから。中に入りたがる\nアイドルもいたが、中には入れなかった…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/830, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\nマッサージチェア。瑞樹が愛用しているもので\n若々しさを支えているものの一つらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/831, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nサマーカーニバルで、みんなが笑顔になった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/832, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルは気持ちをトロピカルに伝えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/833, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。アイドルはファンに夏の魔法をかけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/840, /*level*/1, /*item_detail*/"ちゃぶ台。日本の夏を感じる品々が置かれている。\nアイドルはスイカを食べたり、麦茶を飲んだりして\nくつろぐ。談笑していると、暑さも忘れてしまうらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/841, /*level*/1, /*item_detail*/"座椅子。座り心地がいい。\n実家を思い出すというアイドルもいる。\nたまには郷愁に浸るのもいいかもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/842, /*level*/1, /*item_detail*/"ピンクの布団。\nフカフカで寝心地最高。この布団で眠ると\nいい夢が見られると、アイドルたちが言っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/843, /*level*/1, /*item_detail*/"ブルーの布団。\n熱帯夜でも快適に眠れる布団。ぐっすり眠れて\n体調も万全に。明日のお仕事も頑張れそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/844, /*level*/1, /*item_detail*/"オレンジの布団。\n明るい気分で飛び込んだり、枕を投げたり……\nすると怒られるので、静かに眠ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/845, /*level*/1, /*item_detail*/"金魚すくい。夏祭りで金魚すくいに\nハマったアイドルが持ち込んだもの。\n金魚すくい選手権の開催を予定しているとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/846, /*level*/1, /*item_detail*/"蚊取り豚。日本の夏の風物詩。\nカワイイ顔してなかなかデキるヤツ。\nルームに置いて、アイドルたちを蚊から守ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/847, /*level*/1, /*item_detail*/"扇風機。\n扇風機の前で「あー」って言うと、\n宇宙人になれる…らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/848, /*level*/1, /*item_detail*/"行燈。夏の夜をほのかな明かりで彩る。\n使いどころ次第で、おしゃれも不気味も\n演出できる有能なアイテム。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/849, /*level*/1, /*item_detail*/"市松人形。\nじっと見つめていると……髪が……\n髪が……", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/850, /*level*/1, /*item_detail*/"あさがお。○月☆日。\nおっきなお花が咲いたよ ! きれい !\nみんなでお世話できたのがいちばん楽しかったなー♪ ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/851, /*level*/1, /*item_detail*/"風鈴。涼しげな音色が鳴る。\n風流とは何たるか、アイドルたちが\n理解するきっかけにもなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2013);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/852, /*level*/1, /*item_detail*/"レトロな柱時計。\nそのレトロさが好評。あるアイドルが、\n実家に同じような時計があったと話していた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2014);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/853, /*level*/1, /*item_detail*/"掛け軸。\n鶴は長寿や幸運を象徴する鳥。\nアイドルの活躍を願って飾られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/854, /*level*/1, /*item_detail*/"エアコン。\n暑さに耐えきれないときは、文明の利器に\n頼るのもアリ。消し忘れると怒られるので注意しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/855, /*level*/1, /*item_detail*/"障子の壁。日本旅館のような安らぎを感じられる\nルームにできる。忙しくて、季節を感じる余裕のない\nアイドルたちも、日本の夏を感じながらのんびり……", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/856, /*level*/1, /*item_detail*/"夏の田園風景をテーマにした背景。\n都会では見られない景色が広がっている。\n緑に癒されて、さらにお仕事を頑張れそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/860, /*level*/1, /*item_detail*/"薔薇ンポリン。\n描かれている薔薇はさすがのクオリティ。\n天井に頭をぶつけないように、優雅に跳ぼう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/861, /*level*/1, /*item_detail*/"うどんちゃんとそばくん。\nこれは何だかわかりますか？…まりもです !\n時々歌って、みんなを癒してくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/870, /*level*/1, /*item_detail*/"ぷにぷにのおまんじゅう。\n至高の触感と究極の食感を追求した一品。\nその触り心地に、わきわきしてしまうアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/880, /*level*/1, /*item_detail*/"イベント「Near to You」で獲得したお立ち台。\nここに立ち、扇子を振れば、誰でもたちまち\nイイナオン。踊り方は早苗が教えてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/881, /*level*/1, /*item_detail*/"イベント「Near to You」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルは目標に向かって走り出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
