INSERT INTO `room_item_detail` VALUES (/*room_item_id*/890, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\nフラワースタンド。\n記念すべき1st.Anniversaryをみんなでお祝いしよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001002);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/891, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルは夢中になってメロディを奏でた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
