INSERT INTO `room_item_detail` VALUES (/*room_item_id*/991, /*level*/1, /*item_detail*/"魔女が使うようなアイテムが置かれた机。\n魔導書や天秤など、魔女の必須アイテムがズラリ。\nここで行われるのは禁断の魔術……？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/992, /*level*/1, /*item_detail*/"かぼちゃ型のスツール。\nフワフワしていて、とても座り心地がよい。\n誰かに座られると、かぼちゃが不気味に笑い続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/993, /*level*/1, /*item_detail*/"魔女のほうき。\nハロウィンのPV撮影に使われたもの。\n座ると、月夜を飛んでいる気分を味わえる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000806);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/994, /*level*/1, /*item_detail*/"魔女のベッド。\n天蓋つきの豪華なベッド。眠り心地もばつぐん。\nここで眠ると、魔女になった夢を見られるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/995, /*level*/1, /*item_detail*/"魔法の杖。\nかぼちゃに帽子を被せて、杖をかざすと、\n世にも不思議なことが起こる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1510201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/996, /*level*/1, /*item_detail*/"ふしぎな鏡。この鏡の前に立つと、\n自分の顔ではなく、ふしぎなものが\n映し出されるという。その目で確かめてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1510301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/997, /*level*/1, /*item_detail*/"おばけランプ。\n怖がりのアイドルも全然怖がらないおばけ。\nぴかっと光るのも、またカワイイらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/998, /*level*/1, /*item_detail*/"ハロウィンおもちゃ箱。\nコスプレに最適な仮装グッズが詰まっている。\nみんなで仮装して、トリックオアトリートを言い合おう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1510101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/999, /*level*/1, /*item_detail*/"魔女の棚。\n不気味なグッズが並んでいるが、わかる人には\nたまらないらしい。触るときは気を付けて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1000, /*level*/1, /*item_detail*/"ブキミな肖像画。\n美しい女性が描かれているが、じーっと見ていると、\n不可解なことが起こるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
