INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1131, /*level*/1, /*item_detail*/"ルナが持っているぬいぐるみ。\nShadowverseとのコラボアイテム。\n抱いていると落ち着くというアイドルが続出。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
